/* ===============================
   SIDEBAR DEL CARRITO (Carrito Lateral)
=============================== */
.carrito-sidebar {
  position: fixed; /* Fija el carrito lateral en la pantalla */
  top: 0;
  right: -350px; /* Inicialmente el carrito está fuera de la pantalla */
  width: 350px; /* Ancho del carrito lateral */
  height: 100%; /* Altura completa de la pantalla */
  background: var(--blanco); /* Fondo blanco */
  box-shadow: -2px 0 10px rgba(0, 0, 0, 0.2); /* Sombra para profundidad */
  padding: 20px;
  transition: right 0.3s ease-in-out; /* Animación suave al abrir y cerrar */
  z-index: 1000; /* Asegura que el carrito se muestre sobre otros elementos */
  display: flex;
  flex-direction: column;
  border-left: 3px solid var(--naranja); /* Borde izquierdo de color naranja */
}

/* Cuando el carrito tiene la clase 'activo', se mueve a la derecha */
.carrito-sidebar.activo {
  right: 0; /* Mueve el carrito lateral a la vista */
}

/* Cabecera del carrito */
.carrito-header {
  display: flex;
  justify-content: space-between; /* Distribuye los elementos de forma equitativa */
  align-items: center; /* Centra los elementos verticalmente */
  border-bottom: 1px solid #ddd; /* Línea separadora debajo */
  padding-bottom: 10px;
  margin-bottom: 20px;
  position: relative; /* Necesario para posicionar el botón de cierre */
}

.carrito-header h3 {
  margin: 0;
  font-size: 20px;
  color: var(--naranja); /* Título en color naranja */
  font-weight: bold;
}

/* Estilo del botón de cierre */
#cerrar-carrito {
  background: transparent;
  border: none;
  font-size: 30px;
  color: var(--naranja); /* Color de la X en naranja */
  cursor: pointer;
  position: absolute; /* Posiciona el botón de cierre de forma absoluta */
  top: 10px; /* Ubica el botón cerca de la parte superior */
  right: 10px; /* Ubica el botón cerca del borde derecho */
  transition: color 0.3s ease; /* Efecto de transición en el color */
}

#cerrar-carrito:hover {
  color: #e65500; /* Cambio de color cuando el mouse pasa sobre la X */
}

/* ===============================
  Barra progreso
=============================== */

.barra-progreso {
  width: 100%;             /* El contenedor de la barra ocupa todo el ancho */
  height: 20px;            /* Altura de la barra */
  background-color: #e0e0e0;  /* Fondo gris claro para la barra */
  border-radius: 10px;     /* Bordes redondeados */
  overflow: hidden;
  position: relative;      /* Necesario para posicionar el contenido dentro */
}

.barra-progreso span {
  display: block;
  height: 100%;            /* La barra de progreso ocupa el 100% de la altura */
  background-color: #4caf50;  /* Color verde para la barra de progreso */
  text-align: center;
  line-height: 20px;       /* Centra el texto verticalmente */
  color: white;            /* Color del texto */
}

/* ===============================
   ITEMS EN EL CARRITO
=============================== */
.carrito-items {
  flex: 1; /* Toma el espacio restante disponible */
  overflow-y: auto; /* Habilita el desplazamiento vertical */
  margin: 15px 0;
  max-height: 60vh; /* Limita la altura de los productos */
  padding-right: 5px;
}

.carrito-items .item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 8px 0;
  border-bottom: 1px solid #f0f0f0; /* Separadores entre los productos */
  font-size: 14px;
  transition: background 0.2s ease; /* Efecto de transición al pasar el mouse */
}

.carrito-items .item:hover {
  background: #fafafa; /* Fondo claro cuando se pasa el mouse sobre un producto */
  border-radius: 8px; /* Bordes redondeados al pasar el mouse */
}

.carrito-items img {
  width: 50px;
  height: 50px;
  object-fit: contain; /* Mantiene la proporción de la imagen */
  border-radius: 6px;
  border: 1px solid #ddd; /* Borde gris alrededor de la imagen */
  margin-right: 10px;
}

.carrito-items .precio {
  color: var(--naranja); /* Precio en color naranja */
  font-weight: bold;
}

/* ===============================
   BOTONES EN EL FOOTER
=============================== */

/* Estilo para el enlace "Ver carrito" */
.btn-ver-carrito {
  display: block;
  width: 100%;
  padding: 12px 0;
  margin-top: 10px;
  font-size: 1rem;
  font-weight: bold;
  text-align: center;
  border-radius: 6px;
  text-decoration: none;
  cursor: pointer;
  background: #ff6600; /* Fondo naranja brillante */
  color: white; /* Texto blanco para mejor contraste */
  border: none; /* Sin borde */
  transition: background 0.3s ease; /* Transición suave de color */
}

/* Hover para el botón de "Ver carrito" */
.btn-ver-carrito:hover {
  background: #e65500; /* Fondo naranja más oscuro al pasar el mouse */
}

.carrito-footer .btn-ver-carrito {
  background: #ff6600; /* Fondo naranja para el botón */
  color: white; /* Texto blanco para mejor contraste */
}

.carrito-footer .btn-ver-carrito:hover {
  background: #e65500; /* Fondo naranja más oscuro al pasar el mouse */
}

/* ===============================
   BOTÓN "Ir a pagar" (se mantiene igual)
=============================== */
.carrito-footer .btn-pagar {
  display: block;
  width: 100%;
  padding: 12px 0;
  margin-top: 10px;
  font-size: 1rem;
  font-weight: bold;
  text-align: center;
  border-radius: 6px;
  text-decoration: none;
  cursor: pointer;
  background: #ff6600; /* Fondo naranja brillante */
  color: white; /* Texto blanco para mejor contraste */
  border: none; /* Sin borde */
  transition: background 0.3s ease; /* Transición suave de color */
}

.carrito-footer .btn-pagar:hover {
  background: #e65500; /* Fondo naranja más oscuro al pasar el mouse */
}

/* ===============================
   OVERLAY (Fondo oscuro)
=============================== */
#overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5); /* Fondo oscuro semi-transparente */
  display: none; /* Oculto por defecto */
  z-index: 900; /* Asegura que el overlay esté por encima del contenido */
}

#overlay.activo {
  display: block; /* Muestra el overlay cuando tiene la clase 'activo' */
}


/* ===============================
   MENSAJE FLOTANTE (TOAST)
=============================== */
#mensajeCarrito {
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: var(--verde); /* Fondo verde para el mensaje flotante */
  color: #fff; /* Texto en blanco */
  padding: 10px 20px;
  border-radius: 5px;
  display: none;
  font-family: sans-serif;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2); /* Sombra para el mensaje */
  transition: transform 0.3s ease, opacity 0.3s ease; /* Animación para la aparición */
  opacity: 0;
  transform: translateY(20px);
}

#mensajeCarrito.mostrar {
  opacity: 1;
  transform: translateY(0); /* Aparece desde abajo */
}

/* ===============================
   RESPONSIVE (adaptación para pantallas pequeñas)
=============================== */
@media(max-width: 768px) {
  .carrito-sidebar {
    width: 100%; /* El carrito lateral ocupa toda la pantalla en móviles */
    right: 0; /* Asegura que esté visible al abrir */
  }

  .carrito-items img {
    width: 40px;
    height: 40px; /* Imágenes más pequeñas en pantallas pequeñas */
  }

  .carrito-header h3 {
    font-size: 18px; /* Reduce el tamaño del título */
  }

  .carrito-footer .linea {
    font-size: 14px; /* Reduce el tamaño de la fuente en el resumen */
  }

  .carrito-footer .btn-ver-carrito,
  .carrito-footer .btn-pagar {
    font-size: 0.9rem; /* Reduce el tamaño de los botones */
  }

  /* El carrito lateral no debe tapar la tienda en pantallas pequeñas */
  .carrito-sidebar.activo {
    right: 0; /* Asegura que se mueva completamente hacia la vista */
  }
}

@media(max-width: 480px) {
  .carrito-header h3 {
    font-size: 16px; /* Reduce aún más el tamaño del título */
  }

  .carrito-items .item {
    font-size: 12px; /* Reduce el tamaño de las letras de los items */
  }

  .carrito-footer .btn-ver-carrito,
  .carrito-footer .btn-pagar {
    font-size: 0.8rem; /* Reduce el tamaño de los botones en pantallas muy pequeñas */
  }
}


/* Asegurarnos que el overlay cubra toda la pantalla */
#overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0); /* Elimina el color oscuro, completamente transparente */
  display: none; /* Oculto por defecto */
  z-index: 1001; /* Asegura que el overlay esté por encima de todo */
}

/* Cuando el carrito está activo, el overlay se muestra */
#overlay.activo {
  display: block;
}

@media(max-width: 768px) {
  /* El carrito lateral se mueve usando transform en dispositivos móviles */
  .carrito-sidebar {
    right: -350px; /* Inicialmente fuera de la pantalla */
    transform: translateX(100%); /* Usamos transform para desplazar el carrito */
    transition: transform 0.3s ease-in-out; /* Animación suave */
  }

  /* Cuando el carrito está activo, lo movemos hacia la vista */
  .carrito-sidebar.activo {
    transform: translateX(0); /* Mueve el carrito completamente hacia la vista */
  }
}
.item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 5px;
  margin-bottom: 10px;
  border-bottom: 1px solid #ddd;
}

.item img {
  margin-right: 10px;
}

.item button.eliminar {
  background: none;
  border: none;
  color: red;
  cursor: pointer;
}

.item button.eliminar:hover {
  text-decoration: underline;
}
