+/* ========================================
+   Reset de estilos generales
+   ======================================== */
+* {
+    margin: 0;
+    padding: 0;
+    box-sizing: border-box;
+}
+
+body {
+    font-family: Arial, sans-serif;
+    background-color: #fff;
+    color: #333;
+}
+
+.carrito-page {
+    display: flex;
+    flex-direction: column;
+    height: 100vh;
+}
+
+/* ========================================
+   Estilos para el Header
+   ======================================== */
+
+/* Franja superior */
+.promo-bar {
+    background-color: #FF6600;
+    color: white;
+    text-align: center;
+    padding: 10px 0;
+}
+
+.top-bar {
+    background-color: #f06305;
+    padding: 10px 20px;
+    text-align: right;
+    font-size: 14px;
+}
+
+.top-bar a {
+    color: #FF6600;
+    text-decoration: none;
+    font-weight: bold;
+}
+
+/* ========================================
+   Estilos para el menú lateral
+   ======================================== */
+
+/* Menú lateral visible en pantallas grandes */
+#nav {
+    position: fixed;
+    top: 0;
+    left: -250px;
+    width: 250px;
+    height: 100vh;
+    background-color: #FF6600;
+    box-shadow: 2px 0 10px rgba(0, 0, 0, 0.2);
+    transition: left 0.3s ease-in-out;
+    z-index: 1000;
+}
+
+#nav ul {
+    display: flex;
+    list-style-type: none;
+    flex-direction: column;
+    padding: 0;
+}
+
+#nav ul li {
+    margin-bottom: 20px;
+}
+
+#nav ul li a {
+    text-decoration: none;
+    color: #333;
+    font-size: 18px;
+    font-weight: bold;
+    padding: 15px 20px;
+    display: block;
+}
+
+#nav ul li a:hover {
+    background-color: #FF6600;
+    color: white;
+    border-radius: 5px;
+}
+
+/* Menú móvil */
+.overlay {
+    position: fixed;
+    top: 0;
+    left: 0;
+    width: 100%;
+    height: 100%;
+    background-color: rgba(0, 0, 0, 0.4);
+    display: none;
+    z-index: 1000;
+}
+
+#nav.activo {
+    left: 0;
+}
+
+/* ========================================
+   Barra de progreso
+   ======================================== */
+.progress-bar {
+    background-color: #e0e0e0;
+    border-radius: 10px;
+    padding: 10px;
+    margin-bottom: 30px;
+}
+
+.progress-bar span {
+    font-size: 14px;
+    margin-bottom: 5px;
+}
+
+.progress {
+    background-color: #FF6600;
+    height: 15px;
+    border-radius: 5px;
+    margin-top: 10px;
+}
+
+.progress-fill {
+    background-color: #fff;
+    height: 100%;
+    width: 0%;
+    border-radius: 5px;
+}
+
+/* ========================================
+   Estilo para el carrito
+   ======================================== */
+.carrito-container {
+    display: flex;
+    flex-direction: column;
+    padding: 20px;
+    background-color: #fafafa;
+}
+
+h2 {
+    font-size: 28px;
+    margin-bottom: 20px;
+}
+
+.carrito-layout {
+    display: flex;
+    justify-content: space-between;
+    gap: 30px;
+}
+
+/* Carrito de productos */
+.carrito-items {
+    flex: 1;
+    max-height: 400px;
+    overflow-y: auto;
+}
+
+/* Producto */
+.producto {
+    display: flex;
+    align-items: center;
+    margin-bottom: 20px;
+    border-bottom: 1px solid #ddd;
+    padding-bottom: 20px;
+    gap: 20px;
+}
+
+.producto img {
+    width: 120px;
+    height: 120px;
+    margin-right: 20px;
+    object-fit: contain;
+}
+
+.descripcion p {
+    margin: 0;
+    font-size: 14px;
+    color: #555;
+}
+
+.cantidad {
+    display: flex;
+    align-items: center;
+    gap: 10px;
+}
+
+.cantidad button {
+    background-color: #FF6600;
+    color: white;
+    border: none;
+    padding: 5px 10px;
+    cursor: pointer;
+}
+
+.cantidad input {
+    width: 60px;
+    text-align: center;
+    margin: 0 10px;
+}
+
+.precio {
+    margin-left: auto;
+    font-size: 18px;
+    font-weight: bold;
+}
+
+.eliminar {
+    background-color: red;
+    color: white;
+    border: none;
+    padding: 5px 10px;
