+    cursor: pointer;
+    margin-top: 20px;
+    border-radius: 5px;
+}
+
+#btn-whatsapp:hover {
+    background-color: #128C7E;
+}
+
+#descuento {
+    padding: 5px;
+    width: 70%;
+    margin-right: 10px;
+}
+
+.btn-aplicar {
+    background-color: #FF6600;
+    color: white;
+    border: none;
+    padding: 6px 12px;
+    cursor: pointer;
+}
+
+.btn-aplicar:hover {
+    background-color: #cc5200;
+}
+
+/* ========================================
+   Estilos Responsivos (Pantallas pequeñas)
+   ======================================== */
+
+/* Para dispositivos móviles y pantallas más pequeñas */
+@media (max-width: 768px) {
+
+    /* Menú lateral */
+    #nav {
+        left: -250px;
+        width: 100%;
+        top: 0;
+        z-index: 9999;
+    }
+
+    /* Menú hamburguesa */
+    .menu-toggle {
+        display: block;
+    }
+
+    #nav.activo {
+        left: 0;
+    }
+
+    #nav ul {
+        flex-direction: column;
+        padding: 0;
+    }
+
+    .buscador-container,
+    .acciones-header {
+        display: none;
+    }
+
+    .logo-menu-container {
+        display: flex;
+        justify-content: space-between;
+        align-items: center;
+        width: 100%;
+    }
+
+    /* Barra de progreso */
+    .progress-bar {
+        margin-bottom: 20px;
+    }
+
+    .progress-bar span {
+        font-size: 12px;
+    }
+
+    .progress {
