/* Reset de estilos generales */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    background-color: #fff;
    color: #333;
}

.carrito-page {
    display: flex;
    flex-direction: column;
    height: 100vh;
}

/* Menú lateral visible en pantallas grandes */
#nav {
    position: fixed;              /* Fijo en el lado izquierdo */
    top: 0;
    left: -250px;                 /* Oculto fuera de la pantalla por defecto */
    width: 250px;                 /* Ancho del menú lateral */
    height: 100vh;                /* Alto del menú */
    background-color: #FF6600;    /* Fondo naranja */
    box-shadow: 2px 0 10px rgba(0, 0, 0, 0.2); /* Sombra */
    transition: left 0.3s ease-in-out; /* Transición suave para el movimiento */
    z-index: 1000;                /* Asegura que esté por encima de otros elementos */
}

/* Menú de navegación para pantallas grandes */
.nav {
    display: flex;
    justify-content: flex-end;    /* Alineación a la derecha */
    margin-top: 10px;
    padding-right: 20px;
}

.nav ul {
    display: flex;
    list-style-type: none;        /* Elimina los puntos de la lista */
    flex-direction: column;       /* Coloca los elementos verticalmente */
    padding: 0;                   /* Elimina el relleno */
}

.nav ul li {
    margin-bottom: 20px;          /* Espaciado entre los elementos */
}

.nav ul li a {
    text-decoration: none;        /* Elimina el subrayado */
    color: #333;                  /* Color oscuro para el texto */
    font-size: 18px;              /* Tamaño de fuente */
    font-weight: bold;            /* Negrita */
    padding: 15px 20px;           /* Relleno para darle espacio */
    display: block;               /* Hace que el enlace ocupe toda la línea */
}

/* Estilo al pasar el cursor sobre los elementos del menú */
.nav ul li a:hover {
    background-color: #FF6600;    /* Fondo naranja cuando se pasa el mouse */
    color: white;                 /* Texto blanco */
    border-radius: 5px;           /* Bordes redondeados */
}

/* Estilos del menú móvil (cuando se activa) */
/* Overlay (fondo oscuro detrás del menú lateral) */
.overlay {
    position: fixed;             /* Lo hace fijo en la pantalla */
    top: 0;
    left: 0;
    width: 100%;                 /* Ocupa toda la pantalla */
    height: 100%;                /* Ocupa toda la altura */
    background-color: rgba(0, 0, 0, 0.4); /* Menor opacidad (0.4) para un fondo menos oscuro */
    display: none;               /* Oculto por defecto */
    z-index: 1000;               /* Asegura que esté por encima de otros elementos */
}

/* Estilos para cuando el menú lateral se abre (por defecto en pantallas grandes) */
#nav.activo {
    left: 0;                     /* Menú visible cuando se activa */
}

/* Respuesta para dispositivos pequeños (hamburguesa) */
@media (max-width: 768px) {
    .nav {
        display: none;             /* Ocultamos el menú de navegación en pantallas pequeñas */
    }

    #nav {
        display: none;             /* Ocultamos el menú lateral por defecto en móviles */
    }

    .menu-toggle {
        display: block;            /* Mostramos el botón hamburguesa */
    }

    #nav.activo {
        display: block;            /* Muestra el menú cuando se activa */
    }

    #nav ul {
        flex-direction: column;    /* Asegura que los elementos estén en columna (vertical) */
        padding: 0;
    }
}

/* Estilo para el overlay (fondo oscuro) cuando el menú se activa */
.overlay.activo {
    display: block;              /* Muestra el overlay */
}

/* Estilo para el hover sobre el menú */
#nav:hover {
    left: 0;                     /* Muestra el menú cuando el mouse está sobre él */
}

/* Estilo para cuando el cursor está sobre el área del menú */
#nav:hover {
    left: 0;                     /* Hace que el menú se deslice a la izquierda automáticamente */
}

/* Estilos adicionales para otros elementos */
.promo-bar {
    background-color: #FF6600;
    color: white;
    text-align: center;
    padding: 10px 0;
}

.top-bar {
    background-color: #f06305;
    padding: 10px 20px;
    text-align: right;
    font-size: 14px;
}

.top-bar a {
    color: #FF6600;
    text-decoration: none;
    font-weight: bold;
}

.carrito-container {
    display: flex;
    flex-direction: column;
    padding: 20px;
    background-color: #fafafa;
}

h2 {
    font-size: 28px;
    margin-bottom: 20px;
}

/* Barra de progreso para el carrito */
.progress-bar {
    background-color: #e0e0e0;  /* Fondo gris claro */
    border-radius: 10px;        /* Bordes redondeados */
    padding: 10px;              /* Espaciado interno */
    margin-bottom: 20px;        /* Espaciado inferior */
}

.progress-bar span {
    font-size: 14px;  /* Tamaño de fuente pequeño */
}

.progress {
    background-color: #FF6600;  /* Barra de progreso naranja */
    height: 10px;                /* Altura de la barra */
    border-radius: 5px;         /* Bordes redondeados */
    margin-top: 10px;           /* Espaciado superior */
}

.progress-fill {
    background-color: #fff;    /* Color de la barra de progreso interna */
    height: 100%;
    width: 0%;                 /* Inicia en 0% */
    border-radius: 5px;        /* Bordes redondeados */
}

/* Layout del carrito */
.carrito-layout {
    display: flex;
    justify-content: space-between; /* Espaciado entre los elementos */
    gap: 30px;                     /* Espaciado entre los elementos */
}

.carrito-items {
    flex: 1;  /* Toma el 100% del espacio disponible */
    max-height: 400px; /* Altura máxima para que aparezca la barra de desplazamiento */
    overflow-y: auto;  /* Aparece barra de desplazamiento cuando sea necesario */
}

.producto {
    display: flex;
    align-items: center;    /* Centra los productos verticalmente */
    margin-bottom: 20px;     /* Espaciado inferior */
    border-bottom: 1px solid #ddd; /* Línea divisoria */
    padding-bottom: 10px;    /* Espaciado inferior */
}

.producto img {
    width: 100px;            /* Ancho de la imagen */
    height: 100px;           /* Alto de la imagen */
    margin-right: 20px;      /* Espaciado a la derecha */
}

.descripcion p {
    margin: 0;               /* Elimina márgenes */
    font-size: 14px;          /* Tamaño de fuente pequeño */
}

/* Controles de cantidad */
.cantidad {
    display: flex;
    align-items: center;   /* Centra los controles verticalmente */
    margin-left: 20px;      /* Espaciado a la izquierda */
}

.cantidad button {
    background-color: #FF6600; /* Fondo naranja */
    color: white;              /* Texto blanco */
    border: none;              /* Sin borde */
    padding: 5px 10px;         /* Espaciado interior */
    cursor: pointer;          /* Cambia el cursor a mano */
}

.cantidad input {
    width: 50px;               /* Ancho fijo para el input */
    text-align: center;        /* Alinea el texto en el centro */
    margin: 0 10px;            /* Espaciado horizontal */
}

.precio {
    margin-left: auto;         /* Empuja el precio a la derecha */
    font-size: 18px;           /* Tamaño de fuente grande */
    font-weight: bold;         /* Negrita */
}

.eliminar {
    background-color: red;    /* Fondo rojo para el botón de eliminar */
    color: white;              /* Texto blanco */
    border: none;              /* Sin borde */
    padding: 5px 10px;         /* Espaciado interior */
    cursor: pointer;          /* Cambia el cursor a mano */
    margin-left: 20px;         /* Espaciado a la izquierda */
}

.eliminar:hover {
    background-color: #cc0000; /* Cambia el fondo a un rojo más oscuro al pasar el mouse */
}

.resumen-compra {
    background-color: #fff;    /* Fondo blanco */
    padding: 20px;              /* Espaciado interior */
    border-radius: 10px;        /* Bordes redondeados */
    width: 300px;               /* Ancho fijo para el resumen */
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); /* Sombra sutil */
}

.resumen-compra h3 {
    margin-bottom: 20px;        /* Espaciado inferior */
    font-size: 24px;            /* Tamaño de fuente grande */
}

.resumen-compra div {
    display: flex;
    justify-content: space-between; /* Espaciado entre las líneas */
    margin: 10px 0;               /* Espaciado vertical */
    font-size: 16px;              /* Tamaño de fuente pequeño */
}

.total {
    font-size: 20px;             /* Tamaño de fuente mayor para el total */
    font-weight: bold;           /* Negrita */
    margin-top: 20px;            /* Espaciado superior */
}

#btn-whatsapp {
    background-color: #25D366; /* Fondo verde de WhatsApp */
    color: white;              /* Texto blanco */
    border: none;              /* Sin borde */
    padding: 10px 20px;        /* Espaciado interior */
    width: 100%;               /* Ancho completo */
    cursor: pointer;          /* Cambia el cursor a mano */
    margin-top: 20px;          /* Espaciado superior */
    border-radius: 5px;        /* Bordes redondeados */
    display: flex;             /* Alineación de los elementos internos */
    align-items: center;
    justify-content: center;   /* Centra el contenido */
}

#btn-whatsapp img {
    margin-right: 10px;        /* Espaciado a la derecha de la imagen */
}

#btn-whatsapp:hover {
    background-color: #128C7E; /* Cambia el color a un verde más oscuro al pasar el mouse */
}

/* Estilo para el input del cupón */
#descuento {
    padding: 5px;              /* Espaciado interno */
    width: 80%;                /* Ancho del 80% */
    margin-right: 10px;        /* Espaciado a la derecha */
}

/* Estilo para el botón de aplicar el cupón */
.btn-aplicar {
    background-color: #FF6600; /* Fondo naranja */
    color: white;              /* Texto blanco */
    border: none;              /* Sin borde */
    padding: 6px 12px;         /* Espaciado interior */
    cursor: pointer;          /* Cambia el cursor a mano */
}

.btn-aplicar:hover {
    background-color: #cc5200; /* Fondo más oscuro al pasar el mouse */
}
