+
+/* Logo */
+.logo {
+  display: flex;
+  align-items: center;
+}
+.logo img {
+  height: 60px;
+  margin-right: 15px;
+  border-radius: 50%;
+}
+.logo h1 {
+  font-size: 1.5rem;
+  color: var(--naranja);
+}
+
+/* ===== NAV (menú de navegación) ===== */
+#menu-toggle {
+  display: none;
+  font-size: 26px;
+  background: none;
+  border: none;
+  cursor: pointer;
+}
+#nav ul {
+  display: flex;
+  list-style: none;
+  gap: 20px;
+}
+#nav ul li a {
+  text-decoration: none;
+  color: var(--oscuro);
+  font-weight: bold;
+  padding: 0.4rem 0.8rem;
+  border-radius: 8px;
+  transition: 0.3s;
+}
+#nav ul li a:hover {
+  color: var(--blanco);
+  background: var(--naranja);
+  box-shadow: 0 0 10px var(--naranja);
+}
+
+/* Menú oculto en móviles */
+#nav {
+  display: flex;
+  gap: 1rem;
+}
+
+#menu-toggle {
