+.hero button {
+  background: var(--naranja);
+  color: var(--blanco);
+  border: none;
+  padding: 0.8rem 1.5rem;
+  border-radius: 5px;
+  font-size: 1rem;
+  cursor: pointer;
+  transition: 0.3s;
+}
+.hero button:hover { background: #e65500; }
+
+/* ===================================================
+   PRODUCTOS
+=================================================== */
+.grid {
+  display: grid;
+  grid-template-columns: repeat(auto-fill,minmax(220px,1fr));
+  gap: 20px;
+  padding: 20px;
+}
+.card-producto {
+  background: var(--blanco);
+  border-radius: 8px;
+  box-shadow: 0 4px 10px rgba(0,0,0,0.1);
+  overflow: hidden;
+  text-align: center;
+  transition: 0.3s;
+}
+.card-producto:hover { transform: translateY(-6px); }
+.card-producto img {
+  width: 100%;
+  height: 200px;
+  object-fit: cover;
+}
+.card-producto h3 { margin: 10px 0; font-size: 1.1rem; }
+.card-producto p { color: #666; }
+.card-producto button {
+  margin: 10px 0;
+  background: var(--naranja);
+  border: none;
+  color: var(--blanco);
+  padding: 0.6rem 1.2rem;
+  border-radius: 5px;
+  cursor: pointer;
+}
+
+.card-producto:hover {
+  transform: translateY(-6px);
+  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.15); /* Agregar sombra */
+}
+
+.card-producto button:hover { background: #e65500; }
+/* ===================================================
+   ESTO ES PARA LA OFERTA
+=================================================== */
+
+.grid-ofertas {
+  display: grid;
+  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr)); /* Como antes */
+  gap: 20px;
+  padding: 20px;
+}
+
+.card-oferta {
+  background: var(--blanco);
+  border-radius: 8px;
+  box-shadow: 0 4px 10px rgba(0,0,0,0.1);
+  overflow: hidden;
+  text-align: center;
+  transition: 0.3s;
+  max-width: 100%; /* Asegura que las tarjetas no crezcan más de lo esperado */
+}
+
+/* ===================================================
+   SIDEBAR DEL CARRITO
+=================================================== */
+.carrito-sidebar {
+  position: fixed;
+  top: 0;
+  right: -400px;
+  width: 350px;
+  height: 100%;
+  background: var(--blanco);
+  box-shadow: -2px 0 10px rgba(0,0,0,0.3);
+  transition: right 0.3s ease;
+  z-index: 10000;
+  display: flex;
+  flex-direction: column;
+}
+.carrito-sidebar.activo { right: 0; }
+
+.carrito-header {
+  background: var(--naranja);
+  color: var(--blanco);
+  padding: 1rem;
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+}
+.carrito-items {
+  flex: 1;
+  overflow-y: auto;
+  padding: 1rem;
+}
+.carrito-footer {
+  border-top: 1px solid #ddd;
+  padding: 1rem;
+}
+.carrito-footer .linea {
+  display: flex;
+  justify-content: space-between;
+  margin: 0.3rem 0;
+}
+.carrito-footer .total { font-weight: bold; }
+.btn-pagar,
+.btn-ver-carrito {
+  display: block;
+  width: 100%;
+  margin: 0.4rem 0;
+  padding: 0.8rem;
+  text-align: center;
+  border: none;
+  border-radius: 5px;
+  font-weight: bold;
+  cursor: pointer;
+}
+.btn-pagar { background: var(--naranja); color: var(--blanco); }
+.btn-ver-carrito { background: var(--gris-claro); color: var(--oscuro); }
+
+/* ===================================================
+   FOOTER
+=================================================== */
+footer {
+  background: var(--gris-claro);
+  text-align: center;
+  padding: 1rem;
+}
+footer p { font-size: 0.9rem; }
+footer a { color: var(--naranja); text-decoration: none; }
+footer a:hover { text-decoration: underline; }
+
+footer a {
+  color: var(--naranja);
+  text-decoration: none;
+  font-weight: bold;
+  transition: color 0.3s;
+}
+footer a:hover {
+  color: #e65500; /* Naranja más oscuro en hover */
+}
+
+
+/* ===================================================
+   RESPONSIVE
+=================================================== */
+@media (max-width: 992px) {
+  .grid { grid-template-columns: 1fr; }
+  .hero h2 { font-size: 1.6rem; }
+}
+@media (max-width: 768px) {
+  #menu-toggle { display: block; }
+  #nav {
+    display: none;
+    position: absolute;
+    top: 70px;
+    right: 2rem;
+    width: 200px;
+    background: var(--blanco);
+    border-radius: 8px;
+    box-shadow: 0 4px 10px rgba(0,0,0,0.15);
+    flex-direction: column;
+    padding: 0.8rem;
+  }
+  #nav.show { display: flex; }
+  #nav ul { flex-direction: column; gap: 10px; }
+}
+@media (max-width: 480px) {
+  .logo h1 { font-size: 1.2rem; }
+  .hero h2 { font-size: 1.3rem; }
+}
