+/* ===================================================
+   CSS de la Página Principal
+   Autor: Avifarma
+   Descripción: Estilos generales + carrito + responsive
+=================================================== */
+
+/* ===== VARIABLES ===== */
+:root {
+  --naranja: #ff6600;     /* Color principal */
+  --gris: #f5f5f5;        /* Fondo general */
+  --gris-claro: #f0f0f0;  /* Fondo secundario */
+  --oscuro: #333;         /* Texto principal */
+  --blanco: #fff;         /* Blanco */
+  --carrito-size: 36px;   /* Tamaño del ícono de carrito */
+  --contador-color: #e53935; /* Color de la burbuja contador */
+}
+
+/* ===== RESET ===== */
+* {
+  margin: 0;
+  padding: 0;
+  box-sizing: border-box;
+}
+
+body {
+  font-family: Arial, sans-serif;
+  background: var(--gris);
+  color: var(--oscuro);
+  line-height: 1.6;
+}
+
+/* ===================================================
+   HEADER
+=================================================== */
+header {
+  border-bottom: 1px solid #ddd;
+  position: relative;
+  z-index: 900;
+}
+
+.top-bar {
+  background: var(--naranja);
+  color: var(--blanco);
+  text-align: right;
+  padding: 0.5rem 2rem;
+  font-size: 0.95rem;
+}
+
+.main-header {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 1rem 2rem;
+  flex-wrap: wrap;
+  gap: 1rem;
+}
+
+/* Logo */
+.logo {
+  display: flex;
+  align-items: center;
+}
+.logo img {
+  height: 60px;
+  margin-right: 15px;
+  border-radius: 50%;
+}
+.logo h1 {
+  font-size: 1.5rem;
+  color: var(--naranja);
+}
+
+/* ===== NAV (menú de navegación) ===== */
+#menu-toggle {
+  display: none;
+  font-size: 26px;
+  background: none;
+  border: none;
+  cursor: pointer;
+}
+#nav ul {
+  display: flex;
+  list-style: none;
+  gap: 20px;
+}
+#nav ul li a {
+  text-decoration: none;
+  color: var(--oscuro);
+  font-weight: bold;
+  padding: 0.4rem 0.8rem;
+  border-radius: 8px;
+  transition: 0.3s;
+}
+#nav ul li a:hover {
+  color: var(--blanco);
+  background: var(--naranja);
+  box-shadow: 0 0 10px var(--naranja);
+}
+
+/* Menú oculto en móviles */
+#nav {
+  display: flex;
+  gap: 1rem;
+}
+
+#menu-toggle {
+  display: none;
+  font-size: 2rem;
+  cursor: pointer;
+  background: none;
+  border: none;
+}
+
+@media (max-width: 768px) {
+  #nav {
+    display: none;
+    flex-direction: column;
+    background: #fff;
+    position: absolute;
+    top: 60px;
+    right: 10px;
+    width: 200px;
+    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
+    padding: 1rem;
+  }
+
+  #nav.active {
+    display: flex;
+  }
+
+  #menu-toggle {
+    display: block;
+  }
+}
+
+/* ===== Acciones (buscador + carrito) ===== */
+.acciones-header {
+  display: flex;
+  align-items: center;
+  gap: 0.8rem;
+}
+.search input {
+  padding: 0.4rem;
+  border: 1px solid #ccc;
+  border-radius: 5px;
+}
+.search button {
+  border: none;
+  background: var(--naranja);
+  color: var(--blanco);
+  padding: 0.4rem 0.6rem;
+  border-radius: 5px;
+  cursor: pointer;
+}
+
+/* ===== Carrito ===== */
+.carrito-icono {
+  position: relative;
+  display: inline-flex;
+  justify-content: center;
+  align-items: center;
+  width: var(--carrito-size);
+  height: var(--carrito-size);
+}
+.carrito-icono .icono-img {
+  width: 100%;
+  height: 100%;
+  object-fit: contain;
+}
+#contador-carrito {
+  position: absolute;
+  top: -6px;
+  right: -6px;
+  background: var(--contador-color);
+  color: var(--blanco);
+  font-size: 12px;
+  font-weight: bold;
+  padding: 2px 6px;
+  border-radius: 50%;
+  border: 2px solid var(--blanco);
+  display: grid;
+  place-items: center;
+  min-width: 20px;
+  height: 20px;
+}
+
+/* ===================================================
+   HERO
+=================================================== */
+.hero {
+  background: url("../img/logo/hero.png") center/cover no-repeat;
+  height: 80vh;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  align-items: center;
+  text-align: center;
+  color: var(--blanco);
+  padding: 1rem;
+}
+.hero h2 {
+  font-size: 2rem;
+  margin-bottom: 1rem;
+}
+.hero button {
+  background: var(--naranja);
+  color: var(--blanco);
+  border: none;
+  padding: 0.8rem 1.5rem;
+  border-radius: 5px;
+  font-size: 1rem;
+  cursor: pointer;
+  transition: 0.3s;
+}
+.hero button:hover { background: #e65500; }
+/* =====================
+   PRODUCTOS
+====================== */
+.grid {
+  display: grid;
+  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr)); /* Auto-fit en vez de auto-fill */
+  gap: 20px;
+  padding: 20px;
+}
+
+.card-producto {
+  background: var(--blanco);
+  border-radius: 8px;
+  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
+  overflow: hidden;
+  text-align: center;
+  transition: 0.3s;
+}
+
+.card-producto:hover {
+  transform: translateY(-6px);
+  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.15); /* Sombra más fuerte en hover */
+}
+
+/* Ajuste de la imagen de los productos */
+.card-producto img {
+  width: 100%;
+  height: 200px;
+  object-fit: cover;
+  border-bottom: 1px solid #ddd; /* Línea separadora debajo de la imagen */
+}
+
+.card-producto h3 {
+  margin: 10px 0;
+  font-size: 1.1rem;
+}
+
+.card-producto p {
+  color: #666;
+}
+
+.card-producto button {
+  margin: 10px 0;
+  background: var(--naranja);
+  border: none;
+  color: var(--blanco);
+  padding: 0.6rem 1.2rem;
+  border-radius: 5px;
+  cursor: pointer;
+}
+
+.card-producto button:hover {
+  background: #e65500;
+}
+
+/* =====================
+   OFERTAS
+====================== */
+.grid-ofertas {
+  display: grid;
+  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr)); /* Ajuste con auto-fit */
+  gap: 20px;
+  padding: 20px;
+}
+
+.card-oferta {
+  background: var(--blanco);
+  border-radius: 8px;
+  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
+  overflow: hidden;
+  text-align: center;
+  transition: 0.3s;
+}
+
+.card-oferta img {
+  width: 100%;
+  height: 200px;
+  object-fit: cover;
+}
+
+
+/* ===================================================
+   SIDEBAR DEL CARRITO
+=================================================== */
+.carrito-sidebar {
+  position: fixed;
+  top: 0;
+  right: -400px;
+  width: 350px;
+  height: 100%;
+  background: var(--blanco);
+  box-shadow: -2px 0 10px rgba(0,0,0,0.3);
+  transition: right 0.3s ease;
+  z-index: 10000;
+  display: flex;
+  flex-direction: column;
+}
+.carrito-sidebar.activo { right: 0; }
+
+.carrito-header {
+  background: var(--naranja);
+  color: var(--blanco);
+  padding: 1rem;
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+}
+.carrito-items {
+  flex: 1;
+  overflow-y: auto;
+  padding: 1rem;
+}
+.carrito-footer {
+  border-top: 1px solid #ddd;
+  padding: 1rem;
+}
+.carrito-footer .linea {
+  display: flex;
+  justify-content: space-between;
+  margin: 0.3rem 0;
+}
+.carrito-footer .total { font-weight: bold; }
+.btn-pagar,
+.btn-ver-carrito {
+  display: block;
+  width: 100%;
+  margin: 0.4rem 0;
+  padding: 0.8rem;
+  text-align: center;
+  border: none;
+  border-radius: 5px;
+  font-weight: bold;
+  cursor: pointer;
+}
+.btn-pagar { background: var(--naranja); color: var(--blanco); }
+.btn-ver-carrito { background: var(--gris-claro); color: var(--oscuro); }
+
+/* ===================================================
+   FOOTER
+=================================================== */
+/* ===== FOOTER ===== */
+footer {
+    background-color: #333; /* Color de fondo oscuro */
+    color: #fff; /* Color del texto blanco */
+    padding: 20px 0; /* Espaciado superior e inferior */
+    text-align: center; /* Centrar el texto */
+}
+
+.footer-top {
+    display: flex;
+    justify-content: space-around; /* Espacio entre los elementos */
+    padding-bottom: 20px;
+    border-bottom: 1px solid #444; /* Línea de separación entre las secciones */
+}
+
+.footer-top h3 {
+    margin-bottom: 10px;
+    color: #f1f1f1; /* Color para los títulos */
+}
+
+.footer-top ul {
+    list-style: none;
+    padding: 0;
+}
+
+.footer-top ul li {
+    margin: 5px 0;
+}
+
+.footer-top a {
+    color: #fff; /* Color de los enlaces */
+    text-decoration: none;
+}
+
+.footer-top a:hover {
+    color: #e65500; /* Color de hover para los enlaces */
+}
+
+.footer-bottom {
+    margin-top: 20px;
+    font-size: 0.9rem;
+    color: #ccc; /* Color de texto más suave */
+}
+
+.footer-bottom p {
+    margin: 0;
+}
+
+
+/* ===================================================
+   RESPONSIVE
+=================================================== */
+@media (max-width: 992px) {
+  .grid { grid-template-columns: 1fr; }
+  .hero h2 { font-size: 1.6rem; }
