+  gap: 2rem;
+  margin: 2rem;
+}
 
+/* Estilos comunes para las tarjetas de productos y ofertas */
+.card-producto, .card-oferta {
+  background-color: #fff;
+  padding: 1rem;
+  border-radius: 6px;
+  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
+  text-align: center;
+  transition: transform 0.3s ease, box-shadow 0.3s ease;
+}
 
+/* Efecto hover para las tarjetas */
+.card-producto:hover, .card-oferta:hover {
+  transform: translateY(-5px);
+  box-shadow: 0 8px 28px rgba(0, 0, 0, 0.15);
+}
+
+/* Imágenes dentro de las tarjetas */
+.card-producto img, .card-oferta img {
+  width: 100%;
+  height: 180px;
+  object-fit: cover;
+  border-radius: 6px;
+}
+
+/* Títulos dentro de las tarjetas */
+.card-producto h3, .card-oferta h3 {
+  font-size: 1.2rem;
+  margin: 1rem 0;
+}
+
+/* Descripción dentro de las tarjetas */
+.card-producto p, .card-oferta p {
+  font-size: 1rem;
+  color: #555;
+}
+
+/* Botones dentro de las tarjetas */
+button {
+  background-color: var(--naranja);
+  color: white;
+  padding: 0.5rem 1rem;
+  border: none;
+  border-radius: 4px;
+  cursor: pointer;
+}
+
+button:hover {
+  background-color: #45a049;
+}
+
+/* Estilos para el contenedor de productos y ofertas */
+.productos, .ofertas {
+  margin-bottom: 2rem;
+}
+
+.productos h2, .ofertas h3 {
+  font-size: 1.5rem;
+  font-weight: bold;
+}
+
+/* Estilos para las columnas de productos y ofertas */
+.grid-productos, .grid-ofertas {
+  display: grid;
+  grid-template-columns: repeat(4, 1fr); /* 4 productos u ofertas por fila */
+  gap: 1rem;
+}
+
+/* Responsividad: Ajustar a una columna en pantallas pequeñas */
+@media (max-width: 768px) {
+  .contenedor-principal {
+    grid-template-columns: 1fr;  /* Una columna */
+  }
+
+  .grid-productos, .grid-ofertas {
+    grid-template-columns: 1fr;  /* 1 producto u oferta por fila */
