@@ -0,0 +1,472 @@
+/* ===================================================
+   CSS de la Página Principal
+   Autor: Avifarma
+   Descripción: Estilos generales + carrito + responsive
+=================================================== */
+
+/* ===== VARIABLES ===== */
+:root {
+  --naranja: #ff6600;     /* Color principal */
+  --gris: #f5f5f5;        /* Fondo general */
+  --gris-claro: #f0f0f0;  /* Fondo secundario */
+  --oscuro: #333;         /* Texto principal */
+  --blanco: #fff;         /* Blanco */
+  --carrito-size: 36px;   /* Tamaño del ícono de carrito */
+  --contador-color: #e53935; /* Color de la burbuja contador */
+}
+
+/* Reset */
+* { margin:0; padding:0; box-sizing:border-box; }
+body { font-family: Arial, sans-serif; color: var(--oscuro); line-height: 1.6; }
+
+/* ===== HEADER ===== */
+header {
+  border-bottom: 2px solid #ddd;
+}
+.top-bar {
+  background: var(--naranja);
+  color: var(--blanco);
+  text-align: right;
+  padding: 0.5rem 2rem;
+  font-size: 0.95rem;
+}
+.main-header {
+  display:flex;
+  justify-content: space-between;
+  align-items:center;
+  padding: 1rem 2rem;
+  flex-wrap: wrap;
+}
+/* Logo */
+.logo {
+  display: flex;
+  align-items: center;
+}
+.logo img {
+  height: 60px;
+  margin-right: 15px;
+  border-radius: 50%;
+}
+.logo h1 {
+  font-size: 1.5rem;
+  color: var(--naranja);
+}
+
+/* ===== NAV (menú de navegación) ===== */
+#menu-toggle {
+  display: none;
+  font-size: 26px;
+  background: none;
+  border: none;
+  cursor: pointer;
+}
+#nav ul {
+  display: flex;
+  list-style: none;
+  gap: 20px;
+}
+#nav ul li a {
+  text-decoration: none;
+  color: var(--oscuro);
+  font-weight: bold;
+  padding: 0.4rem 0.8rem;
+  border-radius: 8px;
+  transition: 0.3s;
+}
+#nav ul li a:hover {
+  color: var(--blanco);
+  background: var(--naranja);
+  box-shadow: 0 0 10px var(--naranja);
+}
+
+/* Menú oculto en móviles */
+#nav {
+  display: flex;
+  gap: 1rem;
+}
+
+#menu-toggle {
+  display: none;
+  font-size: 2rem;
+  cursor: pointer;
+  background: none;
+  border: none;
+}
+
+@media (max-width: 768px) {
+  #nav {
+    display: none;
+    flex-direction: column;
+    background: #fff;
+    position: absolute;
+    top: 60px;
+    right: 10px;
+    width: 200px;
+    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
+    padding: 1rem;
+  }
+
+  #nav.active {
+    display: flex;
+  }
+
+  #menu-toggle {
+    display: block;
+  }
+}
+
+/* ===== Acciones (buscador + carrito) ===== */
+.acciones-header {
+  display: flex;
+  align-items: center;
+  gap: 0.8rem;
+}
+.search input {
+  padding: 0.4rem;
+  border: 1px solid #ccc;
+  border-radius: 5px;
+}
+.search button {
+  border: none;
+  background: var(--naranja);
+  color: var(--blanco);
+  padding: 0.4rem 0.6rem;
+  border-radius: 5px;
+  cursor: pointer;
+}
+
+/* ===== Carrito ===== */
+.carrito-icono {
+  position: relative;
+  display: inline-flex;
+  justify-content: center;
+  align-items: center;
+  width: var(--carrito-size);
+  height: var(--carrito-size);
+}
+.carrito-icono .icono-img {
+  width: 100%;
+  height: 100%;
+  object-fit: contain;
+}
+#contador-carrito {
+  position: absolute;
+  top: -6px;
+  right: -6px;
+  background: var(--contador-color);
+  color: var(--blanco);
+  font-size: 12px;
+  font-weight: bold;
+  padding: 2px 6px;
+  border-radius: 50%;
+  border: 2px solid var(--blanco);
+  display: grid;
+  place-items: center;
+  min-width: 20px;
+  height: 20px;
+}
+
+/* ===================================================
+   HERO
+=================================================== */
+.hero {
+  background: url("../img/logo/hero.png") center/cover no-repeat;
+  height: 80vh;
+  display: flex;
+  flex-direction: column;
+  justify-content: center;
+  align-items: center;
+  text-align: center;
+  color: var(--blanco);
+  padding: 1rem;
+}
+.hero h2 {
+  font-size: 2rem;
+  margin-bottom: 1rem;
+}
+.hero button {
+  background: var(--naranja);
+  color: var(--blanco);
+  border: none;
+  padding: 0.8rem 1.5rem;
+  border-radius: 5px;
+  font-size: 1rem;
+  cursor: pointer;
+  transition: 0.3s;
+}
+.hero button:hover { background: #e65500; }
+
+/* ===================================================
+   PRODUCT GRID (Productos y Ofertas)
+=================================================== */
+
+/* Contenedor principal */
+.contenedor-principal {
+  display: grid;
+  grid-template-columns: 1fr 1fr; /* Dos columnas de igual tamaño */
+  gap: 2rem;
+  margin: 2rem;
+}
+
+/* Estilos comunes para las tarjetas de productos y ofertas */
+.card-producto, .card-oferta {
+  background-color: #fff;
+  padding: 1rem;
+  border-radius: 6px;
+  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
+  text-align: center;
+  transition: transform 0.3s ease, box-shadow 0.3s ease;
+}
+
+/* Efecto hover para las tarjetas */
+.card-producto:hover, .card-oferta:hover {
+  transform: translateY(-5px);
+  box-shadow: 0 8px 28px rgba(0, 0, 0, 0.15);
+}
+
+/* Imágenes dentro de las tarjetas */
+.card-producto img, .card-oferta img {
+  width: 100%;
+  height: 180px;
+  object-fit: cover;
+  border-radius: 6px;
+}
+
+/* Títulos dentro de las tarjetas */
+.card-producto h3, .card-oferta h3 {
+  font-size: 1.2rem;
+  margin: 1rem 0;
+}
+
+/* Descripción dentro de las tarjetas */
+.card-producto p, .card-oferta p {
+  font-size: 1rem;
+  color: #555;
+}
+
+/* Botones dentro de las tarjetas */
+.card-producto button, .card-oferta button {
+  background-color: var(--naranja);
+  color: white;
+  padding: 0.5rem 1rem;
+  border: none;
+  border-radius: 4px;
+  cursor: pointer;
+}
+
+.card-producto button:hover, .card-oferta button:hover {
+  background-color: #45a049;
+}
+
+/* Estilos para las columnas de productos y ofertas */
+.grid-productos, .grid-ofertas {
+  display: grid;
+  grid-template-columns: repeat(4, 1fr); /* 4 productos u ofertas por fila */
+  gap: 1rem;
+}
+
+/* Responsividad: Ajustar a una columna en pantallas pequeñas */
+@media (max-width: 768px) {
+  .contenedor-principal {
+    grid-template-columns: 1fr;  /* Una columna */
+  }
+
+  .grid-productos, .grid-ofertas {
+    grid-template-columns: 1fr;  /* 1 producto u oferta por fila */
+  }
+
+  .card-producto, .card-oferta {
+    padding: 1rem; /* Asegúrate de que no se vea muy grande */
+  }
+
+  .card-producto img, .card-oferta img {
+    height: 150px; /* Reduces el tamaño de las imágenes en pantallas pequeñas */
+  }
+}
+
+/* Estilos para el contenedor de productos y ofertas */
+.productos, .ofertas {
+  margin-bottom: 2rem;
+}
+
+.productos h2, .ofertas h3 {
+  font-size: 1.5rem;
+  font-weight: bold;
+}
+
+
+/* PARA HOVER EN OFERTAS */
+.card-oferta {
+  background-color: #ffeb3b;
+  border: 2px solid #f39c12;
+}
+
+.card-oferta:hover {
+  background-color: #f39c12;
