+  gap: 1.5rem;
+  padding: 2rem;
+  max-width: 1200px;
+  margin: 0 auto;
+}
+
+@media (min-width: 1200px) {
+  .novedades .grid { grid-template-columns: repeat(4, 1fr); }
+}
+@media (max-width: 992px) {
+  .novedades .grid { grid-template-columns: repeat(2, 1fr); }
+}
+@media (max-width: 576px) {
