+  margin: 0 auto 12px;
+  object-fit: contain;
+  transition: transform 0.3s ease;
+}
+.novedades .card img:hover { transform: scale(1.1); }
+.novedades .card h3 { text-align: center; margin: 8px 0; font-size: 1.1rem; font-weight: 600; }
+.novedades .card p { text-align: center; font-size: 0.95rem; color: #666; margin-bottom: 12px; }
+.novedades .card button {
+  background: var(--naranja);
+  color: #fff;
+  border: none;
+  padding: 12px 20px;
+  border-radius: 25px;
+  cursor: pointer;
+  font-weight: bold;
+  text-transform: uppercase;
+  transition: background 0.3s ease;
+}
+.novedades .card button:hover { background: #e05500; }
