+  padding: 20px 0;          /* Espaciado */
+  text-align: center;       /* Centra el texto */
+}
+
+/* 🔹 Sección superior del footer */
+.footer-top {
+  display: flex;
+  justify-content: space-around;
+  flex-wrap: wrap;
+  padding-bottom: 20px;
+  border-bottom: 1px solid #444; /* Línea divisoria */
+}
+.footer-top h3 { 
+  margin-bottom: 10px; 
+  color: #f1f1f1;          /* Color gris claro */
+}
+.footer-top ul { 
+  list-style: none; 
+  padding: 0; 
+}
+.footer-top ul li { 
+  margin: 5px 0;           /* Espacio entre los enlaces */
+}
+.footer-top a { 
+  color: #fff;             /* Enlaces blancos */
+  text-decoration: none;   /* Eliminar subrayado */
+}
+.footer-top a:hover { 
+  color: #e65500;          /* Color al pasar el ratón */
+}
+
+/* 🔹 Sección inferior */
+.footer-bottom {
+  margin-top: 20px;
+  font-size: 0.9rem;        /* Tamaño de fuente pequeño */
+  color: #ccc;              /* Texto gris claro */
+}
+.footer-bottom p { 
+  margin: 0; 
+}
+
+/* =========================
+   TOAST - Mensaje Emergente
+=========================== */
+#mensajeCarrito {
+  position: fixed;
+  bottom: 20px;
+  right: 20px;
+  background-color: #28a745; /* Verde para éxito */
+  color: white;
+  padding: 10px 15px;
+  border-radius: 6px;
+  font-size: 16px;
+  display: none;            /* Oculto por defecto */
+  opacity: 0;
+  transform: translateY(20px);  /* Desplazado en el eje Y */
+  transition: opacity 0.5s ease, transform 0.5s ease;
+  z-index: 9999;           /* Muy alto para estar sobre otros elementos */
+}
+
+#mensajeCarrito.mostrar {
+  display: block;           /* Se hace visible */
+  opacity: 1;               /* Se vuelve opaco */
+  transform: translateY(0); /* Se mueve a su posición original */
+}

