+  transition: transform 0.3s, box-shadow 0.3s;
+
+  /* 🔹 Ajuste para alinear los botones */
+  display: flex;
+  flex-direction: column;
+  justify-content: space-between;
+  height: 100%;
+}
+
+.card-producto:hover, .card-oferta:hover { transform: translateY(-10px); }
+
+.card-producto img, .card-oferta img { 
+  max-width: 140px; margin-bottom: 12px; object-fit: contain; 
+}
+
+.card-producto button, .card-oferta button {
+  background: var(--naranja); color: #fff; border: none;
+  padding: 12px 20px; border-radius: 25px;
+  cursor: pointer; font-weight: bold;
+  margin-top: auto; /* 🔹 Empuja el botón hacia abajo */
+}
+
+.card-producto button:hover, .card-oferta button:hover { background: #e05500; }
+
+/* Estilos para los precios y descuentos */
+.precio { 
+  font-size: 1.2rem; 
+  font-weight: bold; 
+  color: #e67e22; 
+  margin-bottom: 12px; 
+  text-align: center;
+}
+
+/* 🔹 Contenedor de precios en dos líneas */
+.precios {
+  display: flex;
+  flex-direction: column;
+  gap: 4px;
+  align-items: center;
+}
+
+.precio-regular {
+  text-decoration: line-through;
+  color: #777;
+  font-size: 14px;
+}
+
+.precio-online {
+  color: #28a745;
+  font-weight: bold;
+  font-size: 18px;
+}
+
+/* 🔹 Precio con descuento adicional (si aplica) */
+.precio-descuento {
+  color: green;
+  font-weight: bold;
+  font-size: 18px;
+}
+
+.descuento {
+  color: red;
+  font-weight: bold;
+  margin-top: 10px;
+}
+
+.ahorro {
+  margin-top: 10px;
+  font-size: 14px;
+  color: #007BFF;
+}
+
+/* Etiqueta de oferta */
+.oferta-tag {
+  position: absolute;
+  top: 10px;
+  left: 10px;
+  background-color: #28a745;
+  color: white;
+  padding: 5px 10px;
+  border-radius: 10px;
+  font-size: 1rem;
+  font-weight: bold;
+  z-index: 10;
+}
+
+/* Botones */
+.card-producto button, 
+.card-oferta button { 
+  background: #ff6600; 
+  color: #fff; 
+  padding: 12px 20px; 
+  border-radius: 25px; 
+  cursor: pointer; 
+  font-weight: bold; 
+  text-transform: uppercase; 
+}
+
+.card-producto button:hover, 
+.card-oferta button:hover { 
+  background: #e05500; 
+}
+
+/* ===================================================
+   06 | ESTILOS DE LOS BOTONES Y PRODUCTOS
+=================================================== */
+.btn-decrementar, .btn-incrementar, .carrito-item-close {
+  background-color: #007BFF;
