+}
+
+.acciones .cantidad { background: #ff9800; }
+.acciones .cantidad:hover { background: #e65100; }
+
+.acciones .eliminar { background: #e74c3c; }
+.acciones .eliminar:hover { background: #c0392b; }
+
+.acciones span {
