+  display: none;
+}
+#overlay-carrito.show { display: block; }
+
+/* ===================================================
+   RESPONSIVE CARRO LATERAL
+=================================================== */
+@media (max-width: 500px) {
+  #carrito-sidebar { width: 100%; }
+  .producto-card { flex-direction: column; align-items: center; text-align: center; }
