+}
+
+/* Efecto hover en las tarjetas */
+.card-servicio:hover {
+    transform: translateY(-10px);
+    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
+}
+
+/* Estilo de los íconos */
+.card-servicio .icon {
+    font-size: 3rem;
+    color: #ff6600; /* Naranja para los íconos */
+    margin-bottom: 15px;
+}
+
+/* Estilo del título y la descripción dentro de las tarjetas */
+.card-servicio h3 {
+    font-size: 1.25rem;
+    color: #ff6600; /* Naranja para el título dentro de la tarjeta */
+    margin-bottom: 10px;
+}
+
+.card-servicio p {
+    font-size: 1rem;
+    color: #666;
+}
+
+/* Estilo para las imágenes de servicio */
+.card-servicio img {
+    width: 100%;
+    height: auto;
+    object-fit: cover;
+    border-radius: 10px;
+}
+
+/* Estilos del contenedor de carrusel */
+.slider-container {
+    position: relative;
+    width: 100%;
+    overflow: hidden;
+}
+
+/* Carrusel de servicios */
+#servicios-carousel {
+    display: flex;  /* Asegura que las tarjetas estén en una fila */
+    flex-wrap: nowrap;  /* Evita que las tarjetas se envuelvan */
+    transition: transform 0.5s ease;  /* Desplazamiento suave */
+}
+
+/* Estilo de las tarjetas dentro del carrusel */
+.card-servicio {
+    flex-shrink: 0;  /* Evita que las tarjetas se compriman */
+    width: 300px;  /* Ajusta el tamaño de las tarjetas */
+    margin: 10px;
+}
+
+/* Botones de navegación */
+.prev, .next {
+    position: absolute;
+    top: 50%;
+    transform: translateY(-50%);
