+}
+
+/* Estilos del contenedor de la cuadrícula */
+.novedades .grid-novedades {
+    display: grid;
+    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
+    gap: 30px;
+    justify-items: center;
+    margin-top: 20px;
+}
+
+/* Responsividad */
+@media (max-width: 1024px) {
+    .novedades .grid-novedades {
+        grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
+    }
+
+    .novedades .card-novedad {
+        max-width: 320px;
+        padding: 20px;
+    }
+
+    .novedades .card-novedad .icon {
+        font-size: 3rem;
+    }
+
+    .novedades .card-novedad h3 {
+        font-size: 1.4rem;
+    }
+
+    .novedades .card-novedad p {
+        font-size: 1rem;
+    }
+}
+
+@media (max-width: 768px) {
+    .novedades .grid-novedades {
+        grid-template-columns: 1fr;
+    }
+
+    .novedades .card-novedad {
+        max-width: 100%;
+        padding: 15px;
+    }
+
+    .novedades .card-novedad .icon {
+        font-size: 2.5rem;
+    }
+
+    .novedades .card-novedad h3 {
+        font-size: 1.3rem;
+    }
+
+    .novedades .card-novedad p {
+        font-size: 0.9rem;
+    }
+}
+
+/* ===================================================
+   010 | TOAST
+=================================================== */
+#mensajeCarrito {
+  position:fixed; bottom:20px; right:20px;
+  background:#28a745; color:#fff; padding:10px 15px;
+  border-radius:6px; font-size:16px;
+  display:none; opacity:0; transform:translateY(20px);
+  transition:.3s; z-index:9999;
+}
+#mensajeCarrito.mostrar { display:block; opacity:1; transform:translateY(0); }
+
+/* ===================================================
+   11 | FOOTER
+=================================================== */
+footer { background: #333; color: #fff; padding: 20px 0; text-align: center; }
+.footer-top {
+  display: flex; justify-content: space-around;
+  flex-wrap: wrap; padding-bottom: 20px;
+  border-bottom: 1px solid #444;
+}
+.footer-top a { color: #fff; text-decoration: none; }
+.footer-top a:hover { color: #e65500; }
+.footer-bottom { margin-top: 20px; font-size: 0.9rem; color: #ccc; }
+
+/* ===================================================
+   12 | RESPONSIVIDAD GLOBAL
+=================================================== */
+@media (max-width: 768px) {
+  #carrito-sidebar { width: 100%; right: -100%; }
+  #carrito-sidebar.activo { transform: translateX(-100%); }
+}

