-
-.main-header img {
-  height: 50px;
-}
-
-.main-header .menu {
-  display: flex;
-  gap: 20px;
-}
-
-.main-header .cart-button {
-  background-color: var(--naranja);
-  padding: 10px 15px;
-  border-radius: 5px;
-  color: white;
-  font-size: 16px;
-  cursor: pointer;
