+        position: relative;
+        overflow: hidden;
+        margin-top: 0;         /* 🔹 Quitamos márgenes extra */
+        padding-top: 60px;     /* 🔹 Espacio dinámico debajo del header */
+        width: 100vw;          /* 🔹 Ocupa todo el ancho de la ventana */
+    }
+
+    .slide {
+        width: 100%;
+        height: auto;          /* 🔹 Ajusta según imagen */
+    }
+
+    .slide img {
+        width: 100vw;          /* 🔹 Cubre todo el ancho del viewport */
+        height: auto;
+        object-fit: cover;     /* 🔹 Recorta sin deformar */
+        display: block;
+    }
+
+    /* --- Botones de navegación --- */
+    .prev, .next {
+        font-size: 1.5rem;
+        padding: 5px;
+        position: absolute;
+        top: 50%;
+        transform: translateY(-50%);
+        z-index: 10;
+        background: rgba(0,0,0,0.3);
+        color: #fff;
+        border-radius: 50%;
+    }
+
+    .prev { left: 15px; }
+    .next { right: 15px; }
+
+    /* --- Puntos de navegación (debajo del banner) --- */
+    .dots-container {
+        position: relative;    /* 🔹 Ya no flotan sobre la imagen */
+        bottom: 0;
+        left: 0;
+        transform: none;
+        display: flex;
+        justify-content: center;
+        gap: 6px;
+        padding: 10px 0;       /* 🔹 Espacio entre el banner y los puntos */
+    }
+
+    .dots-container button {
+        width: 8px;
+        height: 8px;
+        border-radius: 50%;
+        border: none;
+        background: rgba(255,255,255,0.6);
+        transition: background 0.3s;
+    }
+
+    .dots-container .active {
+        background: #ff6600;
+    }
+
+    /* --- Ajuste del header en móviles pequeños --- */
+    .main-header {
+        padding: 4px 8px;       /* 🔹 Menos espacio interno */
+        height: auto;           /* 🔹 Se ajusta al contenido */
+    }
+
+    .main-header .logo img {
+        max-height: 32px;       /* 🔹 Logo más pequeño */
+        width: auto;
+        height: auto;
+    }
+
+    .main-header nav {
+        font-size: 0.85rem;     /* 🔹 Menú más compacto */
+    }
+
+    .main-header .logo,
+    .main-header nav {
+        line-height: 1.2;       /* 🔹 Reduce separación vertical */
+    }
+}
+/* ===================================================
+   05 | GRID PRODUCTOS / OFERTAS
+=================================================== */
+/* === Ajuste para eliminar espacio blanco final === */
+.grid-productos,
+.grid-ofertas {
+  display: grid;
+  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
+  gap: 1.5rem;
+  margin: 0 auto;            /* 👈 antes tenía 20px auto — lo quitamos */
+  padding-bottom: 0;         /* 👈 por si tenía padding extra */
+  max-width: 1200px;
+  justify-content: center;   /* 👌 centra filas incompletas si hubiera */
+}
+
+.card-producto, 
+.card-oferta {
+  background: #fff;
+  padding: 1.5rem;
+  border-radius: 15px;
+  box-shadow: 0 6px 12px rgba(0,0,0,0.1);
+  text-align: center;
+  transition: transform 0.3s, box-shadow 0.3s;
+  display: flex;
+  flex-direction: column;
+  justify-content: space-between;
