+}
+
+.card-producto img, .card-oferta img { 
+  max-width: 140px; 
+  margin-bottom: 12px; 
+  object-fit: contain; 
+}
+
+.card-producto button, .card-oferta button {
+  background: var(--naranja);
+  color: #fff;
+  border: none;
+  padding: 8px 10px;
+  border-radius: 25px;
+  cursor: pointer;
+  font-weight: bold;
+  margin-top: auto; /* 🔹 Empuja el botón hacia abajo */
+  text-transform: uppercase;
+}
+
+.card-producto button:hover, .card-oferta button:hover {
+  background: #e05500;
+}
+
+/* Estilos para los precios y descuentos */
+.precio { 
+  font-size: 1.2rem; 
+  font-weight: bold; 
+  color: #e67e22; 
+  margin-bottom: 12px; 
+  text-align: center;
+}
+
+/* 🔹 Contenedor de precios en dos líneas */
+.precios {
+  display: flex;
+  flex-direction: column;
+  gap: 4px;
+  align-items: center;
+}
+
+.precio-regular {
+  text-decoration: line-through;
+  color: #777;
+  font-size: 14px;
+}
+
+.precio-online {
+  color: #28a745;
+  font-weight: bold;
+  font-size: 18px;
+}
+
+/* 🔹 Precio con descuento adicional (si aplica) */
+.precio-descuento {
+  color: green;
+  font-weight: bold;
+  font-size: 18px;
+}
+
+.descuento {
+  color: red;
+  font-weight: bold;
