+}
+
+/* Estilo para los botones de cantidad e incremento */
+.acciones {
+  display: flex;
+  align-items: center;
+  gap: 10px;
+}
+
+.acciones .cantidad, .acciones .eliminar {
+  width: 32px;
+  height: 32px;
+  border-radius: 50%;
+  border: none;
