+
+.acciones .cantidad { background: #ff9800; }
+.acciones .cantidad:hover { background: #e65100; }
+
+.acciones .eliminar { background: #e74c3c; }
+.acciones .eliminar:hover { background: #c0392b; }
+
+.acciones span {
+  min-width: 30px;
+  text-align: center;
+  font-size: 15px;
+  font-weight: 600;
+}
+
+/* Pie del carrito */
+.carrito-footer {
+  border-top: 1px solid #ddd;
+  padding: 15px;
+  background: #f9f9f9;
+  position: sticky;
+  bottom: 0;
+  left: 0;
