+}
+
+.carrito-footer p {
+  display: flex;
+  justify-content: space-between;
+  margin: 5px 0;
+}
+
+/* Estilos de los botones en el pie del carrito */
+.cart-buttons {
+  display: flex;
+  flex-direction: column;
+  gap: 10px;
+  margin-top: 15px;
+}
+
+#ir-a-pagar {
+  background: #28a745;
+  color: #fff;
+  padding: 10px;
+  border: none;
+  border-radius: 8px;
+  font-weight: bold;
+  cursor: pointer;
+}
+#ir-a-pagar:hover { background: #218838; }
+
+#ver-carrito {
+  background: var(--naranja);
+  color: #fff;
+  padding: 10px;
+  border: none;
+  border-radius: 8px;
