+
+/* ===================================================
+   02.2 | ACCIONES HEADER
+=================================================== */
+.acciones-header { display:flex; align-items:center; gap:0.6rem; }
+.search input { padding:0.4rem; border:1px solid #ccc; border-radius:5px 0 0 5px; }
+.search button { padding:0.4rem 0.6rem; border:none; background:var(--naranja); color:#fff; border-radius:0 5px 5px 0; cursor:pointer; }
+.carrito-icono { position:relative; }
+.carrito-icono img { width:28px; }
+#contador-carrito { position:absolute; top:-5px; right:-8px; background:red; color:#fff; font-size:0.7rem; padding:2px 6px; border-radius:50%; }
+/* ===================================================
+   03 | HERO / SLIDER
+=================================================== */
+.slider-container {
+  position: relative;
+  overflow: hidden;
+  width: 100%;
+  height: 250px; /* 🔽 Antes 400px — más bajo y equilibrado */
+  margin: 0 auto;
+  border-radius: 10px;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+/* --- Slider principal --- */
+.slider {
+  display: flex;
+  transition: transform 0.5s ease-in-out;
+  width: 100%;
+  height: 100%;
+}
+
+.slide {
+  min-width: 100%;
+  height: 100%;
+}
+
+.slide img {
+  width: 100%;
+  height: 100%;
+  object-fit: cover;
+  display: block;
+}
+
+/* --- Botones --- */
+.prev, .next {
+  position: absolute;
+  top: 50%;
+  transform: translateY(-50%);
+  background: rgba(0, 0, 0, 0.5);
+  color: #fff;
+  border: none;
+  font-size: 2rem;
+  padding: 5px 10px;
+  cursor: pointer;
+  border-radius: 50%;
+  z-index: 10;
+  transition: background 0.3s;
+}
+.prev:hover, .next:hover {
+  background: rgba(0, 0, 0, 0.7);
+}
+.prev { left: 15px; }
+.next { right: 15px; }
+
+/* --- Dots --- */
+.dots-container {
+  position: absolute;
+  bottom: 5px; /* ⬇️ Antes estaba en 5px — ahora un poco más abajo */
+  left: 50%;
+  transform: translateX(-50%);
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  gap: 6px;
+  z-index: 20;
+}
+
+.dot {
+  width: 10px;
+  height: 10px;
+  background: rgba(255, 255, 255, 0.8);
+  border-radius: 50%;
+  border: 1px solid rgba(0, 0, 0, 0.2);
+  cursor: pointer;
+  transition: transform 0.3s, background 0.3s;
+}
+
+.dot.active {
+  background: var(--naranja);
+  transform: scale(1.3);
+}
+/* ===================================================
+   03.1 | HERO / SLIDER (MÓVIL)
+=================================================== */
