+    padding-top: 0 !important;
+  }
+
+  .promo-bar,
+  .top-bar {
+    margin-bottom: 0 !important;
+    padding-bottom: 0 !important;
+  }
+
+  .main-header {
+    margin-top: 0 !important;
+    top: auto !important; /* ❗ Importante: evita que tape el slider */
+    position: relative;   /* ✅ Para que no quede fijo sobre el banner */
+  }
+
+  /* --- Contenedor del slider --- */
+  .slider-container {
+    position: relative;
+    overflow: hidden;
+    width: 100%;
+    height: auto;       /* Se adapta al contenido */
