+// Esperamos que el DOM esté completamente cargado
+document.addEventListener('DOMContentLoaded', function () {
+    // Llamamos a la función para cargar los productos y ofertas
+    cargarProductosYOfertas();
+});
+
+// Función para cargar los productos y ofertas desde el archivo JSON
+function cargarProductosYOfertas() {
+    // Usamos fetch para cargar el archivo JSON
+    fetch('productos.json')
+        .then(response => response.json()) // Parseamos el JSON
+        .then(data => {
+            // Llamamos a la función para mostrar los productos
+            mostrarProductos(data.productos);
+            // Llamamos a la función para mostrar las ofertas
+            mostrarOfertas(data.ofertas);
+        })
+        .catch(error => console.error('Error al cargar los productos y ofertas:', error));
+}
+
+// Función para renderizar los productos en la página
+function mostrarProductos(productos) {
+    // Seleccionamos el contenedor donde se mostrarán los productos
+    const contenedorProductos = document.getElementById('productos-grid');
+    
+    // Limpiamos el contenedor antes de agregar nuevos productos
+    contenedorProductos.innerHTML = '';
+
+    // Recorremos el array de productos
+    productos.forEach(producto => {
+        // Creamos el HTML para cada producto
+        const productoHTML = `
+            <div class="card-producto">
+                <img src="${producto.img}" alt="${producto.nombre}">
+                <h3>${producto.nombre}</h3>
+                <p>${producto.descripcion}</p>
+                <p>Precio: $${producto.precio}</p>
+                <button onclick="agregarAlCarrito(${producto.id}, '${producto.nombre}', ${producto.precio}, '${producto.img}')">Agregar al carrito</button>
+            </div>
+        `;
+
+        // Agregamos el HTML del producto al contenedor
+        contenedorProductos.innerHTML += productoHTML;
+    });
+}
+
+// Función para renderizar las ofertas en la página
+function mostrarOfertas(ofertas) {
+    // Seleccionamos el contenedor donde se mostrarán las ofertas
+    const contenedorOfertas = document.getElementById('ofertas-grid');
+    
+    // Limpiamos el contenedor antes de agregar nuevas ofertas
+    contenedorOfertas.innerHTML = '';
+
+    // Recorremos el array de ofertas
+    ofertas.forEach(oferta => {
+        // Creamos el HTML para cada oferta
+        const ofertaHTML = `
+            <div class="card">
+                <img src="${oferta.img}" alt="${oferta.nombre}">
+                <h3>${oferta.nombre}</h3>
+                <p>${oferta.descripcion}</p>
+                <p>Precio: $${oferta.precio}</p>
+                <p><strong>${oferta.descuento}</strong></p> <!-- Muestra el descuento -->
+                <button onclick="agregarAlCarrito(${oferta.id}, '${oferta.nombre}', ${oferta.precio}, '${oferta.img}')">Agregar al carrito</button>
+            </div>
+        `;
+
+        // Agregamos el HTML de la oferta al contenedor
+        contenedorOfertas.innerHTML += ofertaHTML;
+    });
+}
+
+// Función para agregar productos al carrito (esto puede cambiar según tu lógica)
+function agregarAlCarrito(id, nombre, precio, img) {
+    console.log(`Producto agregado al carrito: ${nombre}`);
+    // Aquí puedes agregar la lógica para manejar el carrito, como actualizar el contador, etc.
+}

