+// Función para mostrar los productos
+function mostrarProductos(productos) {
+    const contenedorProductos = document.getElementById('productos-grid');
+    contenedorProductos.innerHTML = '';  // Limpiar el contenedor antes de agregar nuevos productos
+
+    productos.forEach(producto => {
+        // Verificamos si la ruta de la imagen del producto es válida
+        const imageUrl = producto.img || 'default-image.jpg';  // Imagen por defecto si no se proporciona
+
+        // Creamos el div para cada producto
+        const divProducto = document.createElement('div');
+        divProducto.classList.add('card-producto');
+
+        // Creamos la imagen
+        const imgProducto = document.createElement('img');
+        imgProducto.src = imageUrl;
+        imgProducto.alt = producto.nombre;
+
+        // Título del producto
+        const h3Producto = document.createElement('h3');
+        h3Producto.textContent = producto.nombre;
+
+        // Descripción del producto
+        const pDescripcion = document.createElement('p');
+        pDescripcion.textContent = producto.descripcion;
+
+        // Precio del producto
+        const pPrecio = document.createElement('p');
+        pPrecio.textContent = `Precio: S/ ${producto.precio.toFixed(2)}`;
+
+        // Botón para agregar al carrito
+        const buttonAgregar = document.createElement('button');
+        buttonAgregar.textContent = 'Agregar al carrito';
+        buttonAgregar.onclick = () => agregarAlCarrito(producto.id, producto.nombre, producto.precio, producto.img);
+
+        // Agregamos los elementos dentro del div del producto
+        divProducto.appendChild(imgProducto);
+        divProducto.appendChild(h3Producto);
+        divProducto.appendChild(pDescripcion);
+        divProducto.appendChild(pPrecio);
+        divProducto.appendChild(buttonAgregar);
+
+        // Finalmente, agregamos el div al contenedor de productos
+        contenedorProductos.appendChild(divProducto);
+    });
+}
