+// ===================================================
+// Renderizado de Productos y Ofertas
+// ===================================================
+function mostrarItems(lista, contenedorId, tipo = "producto") {
+    const contenedor = document.getElementById(contenedorId);
+    if (!contenedor) {
+        console.error(`⚠️ El contenedor con id "${contenedorId}" no existe en el DOM`);
+        return;
+    }
+
+    contenedor.innerHTML = ""; // Limpiar el contenedor
+
+    lista.forEach(item => {
+        const imageUrl = item.img || "default-image.jpg";
+
+        // Crear la tarjeta
+        const card = document.createElement("div");
+        card.classList.add("card-" + tipo);
+        card.style.position = "relative";
+
+        // 🔹 Etiqueta de promoción por volumen (solo visual)
+        if (item.descuentoVolumen) {
+            const promoTag = document.createElement("div");
+            promoTag.classList.add("promo-tag");
+            promoTag.textContent = item.descuentoVolumen.tipo === "2x1" 
+                ? "Promoción 2x1" 
+                : "3ra unidad al 50%";
+            card.appendChild(promoTag);
+        }
+
+        // Imagen
+        const img = document.createElement("img");
+        img.src = imageUrl;
+        img.alt = item.nombre;
+        card.appendChild(img);
+
+        // Etiqueta de oferta (si existe descuento o porcentaje)
+        if (tipo === "oferta" && (item.descuento || item.precio_con_descuento)) {
+            const etiqueta = document.createElement("div");
+            etiqueta.classList.add("oferta-tag");
+            etiqueta.textContent = item.descuento || "Descuento Especial"; 
+            card.appendChild(etiqueta); 
+        }
+
