+// ===================================================
+// Renderizado de Productos, Ofertas y Novedades
+// ===================================================
+function mostrarItems(lista, contenedorId, tipo = "producto") {
+    const contenedor = document.getElementById(contenedorId);
+    if (!contenedor) {
+        console.error(`⚠️ El contenedor con id "${contenedorId}" no existe en el DOM`);
+        return;
+    }
+
+    contenedor.innerHTML = ""; // Limpiar el contenedor
+
+    lista.forEach(item => {
+        const imageUrl = item.img ?? "default-image.jpg";
+
+        // Crear la tarjeta
+        const card = document.createElement("div");
+        card.classList.add("card-" + tipo);
+        card.style.position = "relative";
+
+        // Solo productos y ofertas usan esta imagen directa
+        if (tipo !== "novedad") {
+            const img = document.createElement("img");
+            img.src = imageUrl;
+            img.alt = item.nombre ?? "Imagen";
+            card.appendChild(img);
+        }
+
+        // ===================================================
+        // 🔹 CASO NOVEDADES (noticias)
+        // ===================================================
+        if (tipo === "novedad") {
+            card.classList.add("card"); // para CSS general
+
+            const imgWrapper = document.createElement("div");
+            imgWrapper.classList.add("img-wrapper");
+
+            const imgNovedad = document.createElement("img");
+            imgNovedad.src = imageUrl;
+            imgNovedad.alt = item.titulo ?? "Imagen de novedad";
+            imgWrapper.appendChild(imgNovedad);
+            card.appendChild(imgWrapper);
+
+            const content = document.createElement("div");
+            content.classList.add("card-content");
+
+            const h3 = document.createElement("h3");
+            h3.textContent = item.titulo;
+
+            const fecha = document.createElement("small");
+            fecha.classList.add("fecha-novedad");
+            fecha.textContent = item.fecha ?? "";
+
+            const pDescripcion = document.createElement("p");
+            pDescripcion.textContent = item.descripcion;
+
+            content.append(h3, fecha, pDescripcion);
+
+            if (item.link || item.boton) {
+                const link = document.createElement("a");
+                link.href = item.link ?? "#";
+                link.textContent = item.boton ?? "Leer más";
+                link.classList.add("btn-leer-mas");
+                link.target = "_blank";
+                content.appendChild(link);
+            }
+
+            card.appendChild(content);
+            contenedor.appendChild(card);
+            return; // 🔹 cortar aquí para novedades
+        }
+
+        // ===================================================
+        // 🔹 CASO PRODUCTOS / OFERTAS
+        // ===================================================
+
+        // Etiqueta de oferta (solo para "ofertas")
+        if (tipo === "oferta" && (item.descuento || item.precio_con_descuento)) {
+            const etiqueta = document.createElement("div");
+            etiqueta.classList.add("oferta-tag");
+            etiqueta.textContent = item.descuento ?? "Descuento Especial";
+            card.appendChild(etiqueta);
+        }
+
+        // Título y descripción
+        const h3 = document.createElement("h3");
+        h3.textContent = item.nombre;
+        const pDescripcion = document.createElement("p");
+        pDescripcion.textContent = item.descripcion;
+        card.appendChild(h3);
+        card.appendChild(pDescripcion);
+
+        // Precios
+        const preciosDiv = document.createElement("div");
+        preciosDiv.classList.add("precios");
+
+        const precioAMostrar = item.precio_con_descuento ?? item.precio;
+
+        if (item.precio_con_descuento && item.precio_con_descuento < item.precio) {
+            const precioRegular = document.createElement("span");
+            precioRegular.classList.add("precio-regular");
+            precioRegular.textContent = `Precio regular: S/ ${item.precio.toFixed(2)}`;
+
+            const precioDescuento = document.createElement("span");
+            precioDescuento.classList.add("precio-descuento");
+            precioDescuento.textContent = `Precio online: S/ ${item.precio_con_descuento.toFixed(2)}`;
+
+            const ahorro = document.createElement("div");
+            ahorro.classList.add("ahorro");
+            ahorro.textContent = `Ahorro: S/ ${item.ahorro?.toFixed(2) ?? "0.00"}`;
+
+            preciosDiv.append(precioRegular, precioDescuento, ahorro);
+        } else {
+            const precioNormal = document.createElement("span");
+            precioNormal.classList.add("precio");
+            precioNormal.textContent = `S/ ${precioAMostrar.toFixed(2)}`;
+
+            if (item.ahorro) {
+                const ahorro = document.createElement("div");
+                ahorro.classList.add("ahorro");
+                ahorro.textContent = `Ahorro: S/ ${item.ahorro.toFixed(2)}`;
+                preciosDiv.append(ahorro);
+            }
+
+            preciosDiv.appendChild(precioNormal);
+        }
+
+        // 🔹 Promoción por volumen o textoPromocion
+        if (item.descuentoVolumen || item.textoPromocion) {
+            const promoInfo = document.createElement("div");
+            promoInfo.classList.add("promo-info");
+
+            if (item.descuentoVolumen) {
+                const promoPercent = item.descuentoVolumen.promo * 100;
+                if (item.descuentoVolumen.compra === 2 && item.descuentoVolumen.aplicaA === 3) {
+                    promoInfo.textContent = `Compra 2 y la 3ra unidad al ${promoPercent}%`;
+                } else if (item.descuentoVolumen.compra === 1 && item.descuentoVolumen.aplicaA === 2) {
+                    promoInfo.textContent = `Compra 1 y la 2da unidad al ${promoPercent}%`;
+                } else if (item.descuentoVolumen.compra === 1 && item.descuentoVolumen.aplicaA === 1) {
+                    promoInfo.textContent = `Llévate ${item.descuentoVolumen.compra} pagando ${item.descuentoVolumen.aplicaA}`;
+                }
+            } else if (item.textoPromocion) {
+                promoInfo.textContent = item.textoPromocion;
+            }
+
+            preciosDiv.appendChild(promoInfo);
+        }
+
+        card.appendChild(preciosDiv);
+
+        // Botón
+        const button = document.createElement("button");
+        button.textContent = "Agregar al carrito";
+        button.onclick = () => agregarAlCarrito(item.id);
+        card.appendChild(button);
+
+        // Insertar tarjeta
+        contenedor.appendChild(card);
+    });
+}
+
+// ===================================================
+// Carrusel de Banners (Promociones)
+// ===================================================
+function mostrarCarrusel(banners) {
+    const contenedor = document.getElementById("slider-banner"); // Usar el id correcto
+    if (!contenedor) {
+        console.error("⚠️ El contenedor con id 'slider-banner' no existe en el DOM");
+        return;
+    }
+
+    contenedor.innerHTML = ""; // Limpiar el contenedor
+
+    // Crear las tarjetas de banner para el carrusel
+    banners.forEach(banner => {
+        const card = document.createElement("div");
+        card.classList.add("banner-card");
+
+        const img = document.createElement("img");
+        img.src = banner.src;
+        img.alt = banner.alt ?? "Imagen de banner";
+
+        card.appendChild(img);
+        contenedor.appendChild(card);
+    });
+
+    // Lógica del carrusel (mover a la siguiente tarjeta)
+    let currentIndex = 0;
+    const cards = document.querySelectorAll('.banner-card');
+    const totalCards = cards.length;
+
+    // Función para actualizar la posición del carrusel
+    function updateCarousel() {
+        const offset = -currentIndex * (cards[0].offsetWidth + 20);  // Ajuste el espacio entre tarjetas
+        contenedor.style.transform = `translateX(${offset}px)`;
+    }
+
+    // Funciones para mover el carrusel
+    document.querySelector('.next').addEventListener('click', () => {
+        currentIndex = (currentIndex + 1) % totalCards;  // Deslizar a la siguiente tarjeta
+        updateCarousel();
+    });
+
+    document.querySelector('.prev').addEventListener('click', () => {
+        currentIndex = (currentIndex - 1 + totalCards) % totalCards;  // Deslizar hacia atrás
+        updateCarousel();
+    });
+
+    // Opcional: Agregar puntos de navegación (si los necesitas)
+    const dotsContainer = document.getElementById("dots");
+    banners.forEach((_, index) => {
+        const dot = document.createElement("span");
+        dot.classList.add("dot");
+        dot.addEventListener("click", () => {
+            currentIndex = index;
+            updateCarousel();
+        });
+        dotsContainer.appendChild(dot);
+    });
+}
+
+// ===================================================
+// Carrusel de Servicios
+// ===================================================
+function mostrarCarruselServicios(servicios) {
+    const contenedor = document.getElementById("servicios-carousel");
+    if (!contenedor) {
+        console.error("⚠️ El contenedor con id 'servicios-carousel' no existe en el DOM");
+        return;
+    }
+
+    contenedor.innerHTML = ""; // Limpiar el contenedor
+
+    // Crear las tarjetas de servicio para el carrusel
+    servicios.forEach(servicio => {
+        const card = document.createElement("div");
+        card.classList.add("card-servicio");
+
+        card.innerHTML = `
+            <img src="${servicio.imagen}" alt="${servicio.titulo}">
+            <h3>${servicio.titulo}</h3>
+            <p>${servicio.descripcion}</p>
+        `;
+
+        contenedor.appendChild(card);
+    });
+
+    // Lógica del carrusel (mover a la siguiente tarjeta)
+    let currentIndex = 0;
+    const cards = document.querySelectorAll('.card-servicio');
+    const totalCards = cards.length;
+
+    // Función para actualizar la posición del carrusel
+    function updateCarousel() {
+        const offset = -currentIndex * (cards[0].offsetWidth + 20);  // Ajuste el espacio entre tarjetas
+        contenedor.style.transform = `translateX(${offset}px)`;
+    }
+
+    // Funciones para mover el carrusel
+    document.getElementById('next-btn').addEventListener('click', () => {
+        currentIndex = (currentIndex + 1) % totalCards;  // Deslizar a la siguiente tarjeta
+        updateCarousel();
+    });
+
+    document.getElementById('prev-btn').addEventListener('click', () => {
+        currentIndex = (currentIndex - 1 + totalCards) % totalCards;  // Deslizar hacia atrás
+        updateCarousel();
+    });
+}
+
+// ===================================================
+// Carga del JSON y Renderizado
+// ===================================================
+fetch("./productos.json") // 🔹 Ajusta la ruta según tu estructura
+    .then(res => res.json())
+    .then(data => {
+        mostrarItems(data.productos, "productos-grid", "producto");
+        mostrarItems(data.ofertas, "ofertas-grid", "oferta");
+        mostrarItems(data.novedades, "novedades-grid", "novedad"); // 📰 Novedades
+        mostrarCarrusel(data.BannerCarrusel);
+        mostrarCarruselServicios(data.servicios);  // Mostrar el carrusel de servicios
+    })
+    .catch(err => {
+        console.error("❌ Error al cargar JSON:", err);
+        const errorMessage = document.createElement("p");
+        errorMessage.textContent = "Hubo un problema al cargar los datos.";
+        document.body.appendChild(errorMessage);
+    });

