+// ===================================================
+// Carrusel de Banners
+// ===================================================
+function mostrarCarrusel(banners) {
+    const slider = document.getElementById("slider-banner");
+    if (!slider) return;
 
+    slider.innerHTML = "";
+
+    banners.forEach(item => {
+        const slide = document.createElement("div");
+        slide.classList.add("slide");
+
+        const img = document.createElement("img");
+        img.src = item.src;
+        img.alt = item.alt;
+
+        slide.appendChild(img);
+        slider.appendChild(slide);
+    });
+
+    let currentIndex = 0;
+    const slides = slider.querySelectorAll(".slide");
+    const totalSlides = slides.length;
+
+    const dotsContainer = document.getElementById("dots");
+    if (dotsContainer) {
+        dotsContainer.innerHTML = "";
+        banners.forEach((_, i) => {
+            const dot = document.createElement("div");
+            dot.classList.add("dot");
+            if (i === 0) dot.classList.add("active");
+            dot.addEventListener("click", () => {
+                currentIndex = i;
+                updateSlider();
+                updateDots();
+            });
+            dotsContainer.appendChild(dot);
+        });
+    }
+
+    function updateDots() {
+        if (!dotsContainer) return;
+        dotsContainer.querySelectorAll(".dot").forEach((dot, i) => {
+            dot.classList.toggle("active", i === currentIndex);
+        });
+    }
+
+    function updateSlider() {
+        const offset = -currentIndex * 100;
+        slider.style.transform = `translateX(${offset}%)`;
+        updateDots();
+    }
+
+    function nextSlide() {
+        currentIndex = (currentIndex + 1) % totalSlides;
+        updateSlider();
+    }
+
+    function prevSlide() {
+        currentIndex = (currentIndex - 1 + totalSlides) % totalSlides;
+        updateSlider();
+    }
+
+    const btnPrev = document.querySelector(".prev");
+    const btnNext = document.querySelector(".next");
+    if (btnPrev && btnNext) {
+        btnPrev.addEventListener("click", prevSlide);
+        btnNext.addEventListener("click", nextSlide);
+    }
+
+    let autoplay = setInterval(nextSlide, 5000);
+    slider.parentElement.addEventListener("mouseenter", () => clearInterval(autoplay));
+    slider.parentElement.addEventListener("mouseleave", () => autoplay = setInterval(nextSlide, 5000));
+
+    updateSlider();
+}
