+// CARRUSEL DE LOS BANNERS
+// ===================================================
+// Primero, define la función
+function mostrarCarruselBanners(banners) {
+    const slider = document.getElementById("slider-banner");
+
+    if (!slider) {
+        console.error("⚠️ No existe el contenedor #slider-banner en el DOM");
+        return;
+    }
+
+    slider.innerHTML = ""; // Limpiar carrusel
+
+    if (!banners || banners.length === 0) {
+        console.error("⚠️ No hay banners en el JSON");
+        return;
+    }
+
+    // Crear slides para cada banner
+    banners.forEach(item => {
+        const slide = document.createElement("div");
+        slide.classList.add("slide");
+
+        const img = document.createElement("img");
+        img.src = item.src;
+        img.alt = item.alt;
+        img.style.width = "100%";  // Aseguramos que las imágenes ocupen todo el ancho
+        img.style.height = "100%"; // Aseguramos que las imágenes ocupen toda la altura
+        img.style.objectFit = "cover"; // Garantizamos que las imágenes se ajusten sin distorsionarse
+
+        slide.appendChild(img);
+        slider.appendChild(slide);
+    });
+
+    // Lógica del carrusel
+    let currentIndex = 0;
+    const slides = slider.querySelectorAll(".slide");
+    const totalSlides = slides.length;
+
+    // Crear los puntos indicadores
+    const dotsContainer = document.getElementById("dots");
+    if (dotsContainer) {
+        dotsContainer.innerHTML = "";
+        banners.forEach((_, index) => {
+            const dot = document.createElement("div");
+            dot.classList.add("dot");
+            if (index === 0) dot.classList.add("active");
+
+            dot.addEventListener("click", () => {
+                currentIndex = index;
+                updateSlider();
+                updateDots();
+            });
+
+            dotsContainer.appendChild(dot);
+        });
+    }
+
+    // Función para actualizar los puntos de navegación
+    function updateDots() {
+        if (!dotsContainer) return;
+        document.querySelectorAll(".dot").forEach((dot, i) => {
+            dot.classList.toggle("active", i === currentIndex);
+        });
+    }
+
+    // Función para actualizar el slider (movimiento del carrusel)
+    function updateSlider() {
+        const slideWidth = slides[0].offsetWidth;  // Obtener el ancho real de cada slide
+        const offset = -currentIndex * slideWidth;  // Calcular el desplazamiento
+        slider.style.transform = `translateX(${offset}px)`;  // Aplicar el desplazamiento
+        updateDots();  // Actualizar los puntos de navegación
+    }
+
+    // Función para ir a la siguiente imagen
+    function nextSlide() {
+        currentIndex = (currentIndex + 1) % totalSlides;
+        updateSlider();
+    }
+
+    // Función para ir a la imagen anterior
+    function prevSlide() {
+        currentIndex = (currentIndex - 1 + totalSlides) % totalSlides;
+        updateSlider();
+    }
+
+    // Botones prev / next
+    const btnPrev = document.querySelector(".prev");
+    const btnNext = document.querySelector(".next");
+
+    if (btnPrev && btnNext) {
+        btnPrev.addEventListener("click", prevSlide);
+        btnNext.addEventListener("click", nextSlide);
+    }
+
+    // Autoplay (desplazamiento automático)
+    let autoplay = setInterval(nextSlide, 5000);
+
+    // Pausa en hover (detener autoplay cuando el ratón esté sobre el carrusel)
+    slider.parentElement.addEventListener("mouseenter", () => {
+        clearInterval(autoplay);
+    });
+    slider.parentElement.addEventListener("mouseleave", () => {
+        autoplay = setInterval(nextSlide, 5000);
+    });
+
+    updateSlider();  // Inicializar el carrusel al cargar las imágenes
+}
