+// ===================================================
+// SERVICIOS
+// ===================================================
+function mostrarCarruselServicios(servicios) {
+    const slider = document.getElementById("slider-servicios");
+    if (!slider) return;
+
+    const contenedor = slider.querySelector("#servicios-carousel");
+    const nextButton = slider.querySelector(".next-servicio");
+    const prevButton = slider.querySelector(".prev-servicio");
+    const dotsContainer = slider.querySelector(".dots-servicios");
+
+    contenedor.innerHTML = "";
+
+    servicios.forEach(servicio => {
+        const card = document.createElement("div");
+        card.className = "card-servicio";
+        card.innerHTML = `
+            <img src="${servicio.imagen}" alt="${servicio.titulo}">
+            <h3>${servicio.titulo}</h3>
+            <p>${servicio.descripcion}</p>
+        `;
+        contenedor.appendChild(card);
+    });
+
+    const cards = contenedor.querySelectorAll(".card-servicio");
+    if (!cards.length) return;
+
+    let currentIndex = 0;
+
+    // ancho real de cada tarjeta (incluye márgenes)
+    let cardWidth = cards[0].offsetWidth +
+        parseInt(getComputedStyle(cards[0]).marginLeft) +
+        parseInt(getComputedStyle(cards[0]).marginRight);
+
+    const totalCards = cards.length;
+
+    // calcular cuántas tarjetas caben en pantalla
+    const visibleWidth = slider.offsetWidth;
+    const visibleCards = Math.floor(visibleWidth / cardWidth) || 1;
+
+    // 👉 evita huecos al final
+    const maxIndex = Math.max(totalCards - visibleCards, 0);
+
+    function updateCarousel() {
+        if (currentIndex > maxIndex) currentIndex = maxIndex;
+        if (currentIndex < 0) currentIndex = 0;
+
+        const offset = -currentIndex * cardWidth;
+        contenedor.style.transform = `translateX(${offset}px)`;
+        updateDots();
+    }
+
+    // Crear dots dinámicos
+    if (dotsContainer) {
+        dotsContainer.innerHTML = "";
+        for (let i = 0; i <= maxIndex; i++) {
+            const dot = document.createElement("div");
+            dot.classList.add("dot");
+            if (i === 0) dot.classList.add("active");
+
+            dot.addEventListener("click", () => {
+                currentIndex = i;
+                updateCarousel();
+                resetAutoSlide();
+            });
+
+            dotsContainer.appendChild(dot);
+        }
+    }
+
+    function updateDots() {
+        if (!dotsContainer) return;
+        dotsContainer.querySelectorAll(".dot").forEach((dot, i) => {
+            dot.classList.toggle("active", i === currentIndex);
+        });
+    }
+
+    // Botones de navegación
+    if (nextButton) {
+        nextButton.style.display = "block";
+        nextButton.addEventListener("click", () => {
+            currentIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
+            updateCarousel();
+            resetAutoSlide();
+        });
+    }
+
+    if (prevButton) {
+        prevButton.style.display = "block";
+        prevButton.addEventListener("click", () => {
+            currentIndex = (currentIndex > 0) ? currentIndex - 1 : maxIndex;
+            updateCarousel();
+            resetAutoSlide();
+        });
+    }
+
+    // Auto slide cada 4s
+    let autoSlide = setInterval(() => {
+        currentIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
+        updateCarousel();
+    }, 4000);
+
+    // Resetear auto slide cuando el usuario interactúe
+    function resetAutoSlide() {
+        clearInterval(autoSlide);
+        autoSlide = setInterval(() => {
+            currentIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
+            updateCarousel();
+        }, 4000);
+    }
+
+    // Inicializar
+    updateCarousel();
+}
