+            const ulSub = document.createElement("ul");
+            cat.subcategorias.forEach(sub => {
+                const subLi = document.createElement("li");
+                subLi.textContent = sub;
+                ulSub.appendChild(subLi);
+            });
+            li.appendChild(ulSub);
+        }
+
+        menu.appendChild(li);
+    });
+}
