+// ===================================================
+// CARRUSEL DE LOS BANNERS
+// ===================================================
+// Primero, define la función
+function mostrarCarruselBanners(banners) {
+    const slider = document.getElementById("slider-banner");
+
+    if (!slider) {
+        console.error("⚠️ No existe el contenedor #slider-banner en el DOM");
+        return;
+    }
+
+    slider.innerHTML = ""; // Limpiar carrusel
+
+    if (!banners || banners.length === 0) {
+        console.error("⚠️ No hay banners en el JSON");
+        return;
+    }
+
+    // Crear slides para cada banner
+    banners.forEach(item => {
+        const slide = document.createElement("div");
+        slide.classList.add("slide");
+
+        const img = document.createElement("img");
+        img.src = item.src;
+        img.alt = item.alt;
+        img.style.width = "100%";  // Aseguramos que las imágenes ocupen todo el ancho
+        img.style.height = "100%"; // Aseguramos que las imágenes ocupen toda la altura
+        img.style.objectFit = "cover"; // Garantizamos que las imágenes se ajusten sin distorsionarse
+
+        slide.appendChild(img);
+        slider.appendChild(slide);
+    });
+
+    // Lógica del carrusel
+    let currentIndex = 0;
+    const slides = slider.querySelectorAll(".slide");
+    const totalSlides = slides.length;
+
+    // Crear los puntos indicadores
+    const dotsContainer = document.getElementById("dots");
+    if (dotsContainer) {
+        dotsContainer.innerHTML = "";
+        banners.forEach((_, index) => {
+            const dot = document.createElement("div");
+            dot.classList.add("dot");
+            if (index === 0) dot.classList.add("active");
+
+            dot.addEventListener("click", () => {
+                currentIndex = index;
+                updateSlider();
+                updateDots();
+            });
+
+            dotsContainer.appendChild(dot);
+        });
+    }
+
+    // Función para actualizar los puntos de navegación
+    function updateDots() {
+        if (!dotsContainer) return;
+        document.querySelectorAll(".dot").forEach((dot, i) => {
+            dot.classList.toggle("active", i === currentIndex);
+        });
+    }
+
+    // Función para actualizar el slider (movimiento del carrusel)
+    function updateSlider() {
+        const slideWidth = slides[0].offsetWidth;  // Obtener el ancho real de cada slide
+        const offset = -currentIndex * slideWidth;  // Calcular el desplazamiento
+        slider.style.transform = `translateX(${offset}px)`;  // Aplicar el desplazamiento
+        updateDots();  // Actualizar los puntos de navegación
+    }
+
+    // Función para ir a la siguiente imagen
+    function nextSlide() {
+        currentIndex = (currentIndex + 1) % totalSlides;
+        updateSlider();
+    }
+
+    // Función para ir a la imagen anterior
+    function prevSlide() {
+        currentIndex = (currentIndex - 1 + totalSlides) % totalSlides;
+        updateSlider();
+    }
+
+    // Botones prev / next
+    const btnPrev = document.querySelector(".prev");
+    const btnNext = document.querySelector(".next");
+
+    if (btnPrev && btnNext) {
+        btnPrev.addEventListener("click", prevSlide);
+        btnNext.addEventListener("click", nextSlide);
+    }
+
+    // Autoplay (desplazamiento automático)
+    let autoplay = setInterval(nextSlide, 5000);
+
+    // Pausa en hover (detener autoplay cuando el ratón esté sobre el carrusel)
+    slider.parentElement.addEventListener("mouseenter", () => {
+        clearInterval(autoplay);
+    });
+    slider.parentElement.addEventListener("mouseleave", () => {
+        autoplay = setInterval(nextSlide, 5000);
+    });
+
+    updateSlider();  // Inicializar el carrusel al cargar las imágenes
+}
+
+// ===================================================
+// SERVICIOS
+// ===================================================
+function mostrarCarruselServicios(servicios) {
+    const slider = document.getElementById("slider-servicios");
+    if (!slider) return;
+
+    const contenedor = slider.querySelector("#servicios-carousel");
+    const nextButton = slider.querySelector(".next-servicio");
+    const prevButton = slider.querySelector(".prev-servicio");
+    const dotsContainer = slider.querySelector(".dots-servicios");
+
+    contenedor.innerHTML = "";
+
+    servicios.forEach(servicio => {
+        const card = document.createElement("div");
+        card.className = "card-servicio";
+        card.innerHTML = `
+            <img src="${servicio.imagen}" alt="${servicio.titulo}">
+            <h3>${servicio.titulo}</h3>
+            <p>${servicio.descripcion}</p>
+        `;
+        contenedor.appendChild(card);
+    });
+
+    const cards = contenedor.querySelectorAll(".card-servicio");
+    if (!cards.length) return;
+
+    let currentIndex = 0;
+
+    // ancho real de cada tarjeta (incluye márgenes)
+    let cardWidth = cards[0].offsetWidth +
+        parseInt(getComputedStyle(cards[0]).marginLeft) +
+        parseInt(getComputedStyle(cards[0]).marginRight);
+
+    const totalCards = cards.length;
+
+    // calcular cuántas tarjetas caben en pantalla
+    const visibleWidth = slider.offsetWidth;
+    const visibleCards = Math.floor(visibleWidth / cardWidth) || 1;
+
+    // 👉 evita huecos al final
+    const maxIndex = Math.max(totalCards - visibleCards, 0);
+
+    function updateCarousel() {
+        if (currentIndex > maxIndex) currentIndex = maxIndex;
+        if (currentIndex < 0) currentIndex = 0;
+
+        const offset = -currentIndex * cardWidth;
+        contenedor.style.transform = `translateX(${offset}px)`;
+        updateDots();
+    }
+
+    // Crear dots dinámicos
+    if (dotsContainer) {
+        dotsContainer.innerHTML = "";
+        for (let i = 0; i <= maxIndex; i++) {
+            const dot = document.createElement("div");
+            dot.classList.add("dot");
+            if (i === 0) dot.classList.add("active");
+
+            dot.addEventListener("click", () => {
+                currentIndex = i;
+                updateCarousel();
+                resetAutoSlide();
+            });
+
+            dotsContainer.appendChild(dot);
+        }
+    }
+
+    function updateDots() {
+        if (!dotsContainer) return;
+        dotsContainer.querySelectorAll(".dot").forEach((dot, i) => {
+            dot.classList.toggle("active", i === currentIndex);
+        });
+    }
+
+    // Botones de navegación
+    if (nextButton) {
+        nextButton.style.display = "block";
+        nextButton.addEventListener("click", () => {
+            currentIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
+            updateCarousel();
+            resetAutoSlide();
+        });
+    }
+
+    if (prevButton) {
+        prevButton.style.display = "block";
+        prevButton.addEventListener("click", () => {
+            currentIndex = (currentIndex > 0) ? currentIndex - 1 : maxIndex;
+            updateCarousel();
+            resetAutoSlide();
+        });
+    }
+
+    // Auto slide cada 4s
+    let autoSlide = setInterval(() => {
+        currentIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
+        updateCarousel();
+    }, 4000);
+
+    // Resetear auto slide cuando el usuario interactúe
+    function resetAutoSlide() {
+        clearInterval(autoSlide);
+        autoSlide = setInterval(() => {
+            currentIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
+            updateCarousel();
+        }, 4000);
+    }
+
+    // Inicializar
+    updateCarousel();
+}
+
+// =========================
+// CATEGORÍAS
+// =========================
+
+// Función para mostrar las categorías y subcategorías
+function mostrarCategorias(categorias) {
+  const menu = document.getElementById("menu-categorias");
+  if (!menu) {
+    console.error("⚠️ El contenedor #menu-categorias no existe en el DOM");
+    return;
+  }
+
+  menu.innerHTML = ""; // Limpiar el menú antes de agregar nuevas categorías
+
+  categorias.forEach(cat => {
+    // Crear un <li> para cada categoría con el texto "Categoría:"
+    const li = document.createElement("li");
+    li.textContent = `Categoría: ${cat.nombre}`;
+
+    // Si la categoría tiene subcategorías, las mostramos en una lista anidada
+    if (cat.subcategorias && cat.subcategorias.length > 0) {
+      const ulSub = document.createElement("ul");
+      cat.subcategorias.forEach(sub => {
+        const subLi = document.createElement("li");
+        subLi.textContent = sub.nombre;
+        
+        // Agregar un evento para mostrar productos al hacer clic en una subcategoría
+        subLi.addEventListener('click', () => {
+          mostrarProductos(sub.id); // Llamar a la función para mostrar productos de esta subcategoría
+        });
+
+        ulSub.appendChild(subLi);
+      });
+      li.appendChild(ulSub); // Añadimos las subcategorías al <li> de la categoría
+    }
+
+    // Añadir la categoría al menú
+    menu.appendChild(li);
+
+    // Agregar un evento para mostrar/ocultar subcategorías al hacer clic
+    li.addEventListener('click', () => {
+      li.classList.toggle('active'); // Alterna la clase .active
+    });
+  });
+}
+
+// Función para mostrar los productos de una subcategoría
+function mostrarProductos(subcategoriaId) {
+  const contenedorProductos = document.getElementById("contenedor-productos");
+  if (!contenedorProductos) {
+    console.error("⚠️ El contenedor #contenedor-productos no existe en el DOM");
+    return;
+  }
+
+  contenedorProductos.innerHTML = ""; // Limpiar el contenedor antes de agregar nuevos productos
+
+  // Filtrar los productos por subcategoría
+  const productosFiltrados = productos.filter(producto => producto.subcategoria === subcategoriaId);
+
+  // Mostrar los productos de la subcategoría
+  productosFiltrados.forEach(producto => {
+    const productoElement = document.createElement("div");
+    productoElement.classList.add("producto");
+
+    const productoImg = document.createElement("img");
+    productoImg.src = producto.img;
+    productoImg.alt = producto.nombre;
+
+    const productoNombre = document.createElement("h3");
+    productoNombre.textContent = producto.nombre;
+
+    const productoPrecio = document.createElement("p");
+    productoPrecio.textContent = `Precio: S/. ${producto.precio.toFixed(2)}`;
+
+    productoElement.appendChild(productoImg);
+    productoElement.appendChild(productoNombre);
+    productoElement.appendChild(productoPrecio);
+
+    contenedorProductos.appendChild(productoElement);
+  });
+}
+
+// =========================
+// TOPBAR
+// =========================
+function mostrarTopBar(data) {
+    const topBar = document.getElementById("top-bar");
+    if (!topBar || !data.topBar) {
+        console.error("⚠️ No existe el contenedor #top-bar o no hay datos para el top bar.");
