+    topBar.innerHTML = `
+        <span class="titulo">${data.topBar.titulo ?? ""}</span>
+        <span class="direccion">${data.topBar.direccion ?? ""}</span>
+        <a href="mailto:${data.topBar.email ?? ""}" class="email">${data.topBar.email ?? ""}</a>
+    `;
+}
