document.addEventListener('DOMContentLoaded', function() {
  fetch('productos.json')  // Asegúrate de que la ruta sea correcta
    .then(response => response.json())  // Parsear los datos JSON
    .then(data => renderProductos(data))  // Llamada a la función para renderizar los productos
    .catch(error => console.error('Error al cargar los productos:', error)); // Manejo de errores
});

// Función para renderizar los productos
function renderProductos(productos) {
  const contenedorProductos = document.getElementById('productos-grid');  // El contenedor donde se mostrarán los productos
  contenedorProductos.innerHTML = '';  // Limpiar el contenedor antes de agregar los productos

  // Iteramos sobre cada producto para crear y mostrar su tarjeta
  productos.forEach(producto => {
    const productoElemento = document.createElement('div');
    productoElemento.classList.add('card-producto');  // Añadir la clase 'card-producto' a cada tarjeta
    
    // Contenido de la tarjeta con la información del producto
    productoElemento.innerHTML = `
      <img src="${producto.imagen}" alt="${producto.nombre}" class="producto-imagen">
      <h3>${producto.nombre}</h3>
      <p>${producto.descripcion}</p>
      <p>Precio: S/ ${producto.precio}</p>
      <button onclick="agregarAlCarrito({id: ${producto.id}, nombre: '${producto.nombre}', precio: ${producto.precio}, img: '${producto.imagen}'})">Agregar al carrito</button>
    `;
    
    // Agregar la tarjeta de producto al contenedor
    contenedorProductos.appendChild(productoElemento);
  });
}

